import React, { useEffect, useState } from "react";
import { getSupplierById, updateSupplier } from "../../api";
import SupplierAdd from "../../components/Supplier/SupplierAdd";

export default function SupplierProfile({ supplierId }) {
  const [supplier, setSupplier] = useState(null);
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    getSupplierById(supplierId).then(setSupplier);
  }, [supplierId]);

  const handleUpdate = async (form) => {
    await updateSupplier(supplierId, form);
    setSupplier(form);
    setEditing(false);
  };

  if (!supplier) return <div>Loading...</div>;

  return (
    <div>
      <h2>My Profile</h2>
      {!editing ? (
        <div>
          <p><b>Company Name:</b> {supplier.companyName}</p>
          <p><b>Email:</b> {supplier.email}</p>
          <p><b>Phone:</b> {supplier.phone}</p>
          <p><b>Address:</b> {supplier.address}</p>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </div>
      ) : (
        <SupplierAdd
          supplier={supplier}
          isEdit
          onAdd={handleUpdate}
          onCancel={() => setEditing(false)}
        />
      )}
    </div>
  );
}
