import React, { useEffect, useState } from "react";
import { getProducts, addProduct, updateProduct } from "../../api";
import "../../components/Product/ProductAdd.css";
import "../../components/Product/ProductEdit.css";

// Simple Modal component
function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function ManagerProducts() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [addForm, setAddForm] = useState({ name: "", description: "", price: "", stock: "", supplierId: "", category: "ELECTRONICS" });
  const [editForm, setEditForm] = useState({ name: "", description: "", price: "", stock: "", supplierId: "", category: "ELECTRONICS" });
  const [errorMsg, setErrorMsg] = useState("");

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch {
      // Handle error as needed
    }
  };

  const handleSearch = e => setSearch(e.target.value);

  const filteredProducts = products.filter(
    p => p.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleEditProduct = product => {
    setEditingProduct(product);
    setEditForm({
      name: product.name,
      description: product.description || "",
      price: product.price,
      stock: product.stock,
      supplierId: product.supplierId || "",
      category: product.category || "ELECTRONICS"
    });
  };

  const handleUpdateProduct = async e => {
    e.preventDefault();
    setErrorMsg("");
    try {
      await updateProduct(editingProduct.id, {
        ...editForm,
        price: Number(editForm.price),
        stock: Number(editForm.stock),
        supplierId: Number(editForm.supplierId)
      });
      setEditingProduct(null);
      setEditForm({ name: "", description: "", price: "", stock: "", supplierId: "", category: "ELECTRONICS" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to update product");
    }
  };

  const handleAddProduct = async e => {
    e.preventDefault();
    setErrorMsg("");
    try {
      await addProduct({
        ...addForm,
        price: Number(addForm.price),
        stock: Number(addForm.stock),
        supplierId: Number(addForm.supplierId)
      });
      setShowAddForm(false);
      setAddForm({ name: "", description: "", price: "", stock: "", supplierId: "", category: "ELECTRONICS" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to add product");
    }
  };

  return (
    <div>
      <h2>Products</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <div style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={handleSearch}
          style={{
            padding: 6,
            width: 220,
            background: "transparent",
            border: "1px solid #080406ff",
            color: "#0a0508ff",
            borderRadius: "6px"
          }}
        />
        <button
          onClick={() => setShowAddForm(true)}
          style={{
            marginLeft: 16,
            background: "transparent",
            border: "1px solid #0e0609ff",
            color: "#000000ff",
            borderRadius: "6px",
            padding: "6px 16px",
            cursor: "pointer"
          }}
        >
          Add Product
        </button>
      </div>

      {/* Add Product Modal */}
      <Modal open={showAddForm} onClose={() => setShowAddForm(false)}>
        <form className="product-add-form" onSubmit={handleAddProduct}>
          <input
            required
            placeholder="Name"
            value={addForm.name}
            onChange={e => setAddForm(f => ({ ...f, name: e.target.value }))}
          />
          <input
            placeholder="Description"
            value={addForm.description || ""}
            onChange={e => setAddForm(f => ({ ...f, description: e.target.value }))}
          />
          <input
            required
            type="number"
            placeholder="Price"
            value={addForm.price}
            onChange={e => setAddForm(f => ({ ...f, price: e.target.value }))}
          />
          <input
            required
            type="number"
            placeholder="Stock"
            value={addForm.stock}
            onChange={e => setAddForm(f => ({ ...f, stock: e.target.value }))}
          />
          <select
            name="category"
            value={addForm.category || "ELECTRONICS"}
            onChange={e => setAddForm(f => ({ ...f, category: e.target.value }))}
            required
          >
            <option value="ELECTRONICS">ELECTRONICS</option>
            <option value="GROCERY">GROCERY</option>
            <option value="CLOTHING">CLOTHING</option>
          </select>
          <input
            name="supplierId"
            type="number"
            placeholder="Supplier ID"
            value={addForm.supplierId}
            onChange={e => setAddForm(f => ({ ...f, supplierId: e.target.value }))}
          />
          <div style={{ display: "flex", gap: 8 }}>
            <button type="submit">Add Product</button>
            <button type="button" onClick={() => setShowAddForm(false)}>Cancel</button>
          </div>
        </form>
      </Modal>

      {/* Edit Product Modal */}
      <Modal open={!!editingProduct} onClose={() => setEditingProduct(null)}>
        {editingProduct && (
          <form className="product-edit-form" onSubmit={handleUpdateProduct}>
            <input
              required
              placeholder="Name"
              value={editForm.name}
              onChange={e => setEditForm(f => ({ ...f, name: e.target.value }))}
            />
            <input
              placeholder="Description"
              value={editForm.description || ""}
              onChange={e => setEditForm(f => ({ ...f, description: e.target.value }))}
            />
            <input
              required
              type="number"
              placeholder="Price"
              value={editForm.price}
              onChange={e => setEditForm(f => ({ ...f, price: e.target.value }))}
            />
            <input
              required
              type="number"
              placeholder="Stock"
              value={editForm.stock}
              onChange={e => setEditForm(f => ({ ...f, stock: e.target.value }))}
            />
            <select
              name="category"
              value={editForm.category || "ELECTRONICS"}
              onChange={e => setEditForm(f => ({ ...f, category: e.target.value }))}
              required
            >
              <option value="ELECTRONICS">ELECTRONICS</option>
              <option value="GROCERY">GROCERY</option>
              <option value="CLOTHING">CLOTHING</option>
            </select>
            <input
              name="supplierId"
              type="number"
              placeholder="Supplier ID"
              value={editForm.supplierId}
              onChange={e => setEditForm(f => ({ ...f, supplierId: e.target.value }))}
            />
            <div style={{ display: "flex", gap: 8 }}>
              <button type="submit">Save</button>
              <button type="button" onClick={() => setEditingProduct(null)}>Cancel</button>
            </div>
          </form>
        )}
      </Modal>

      <table
        border="1"
        cellPadding="8"
        style={{
          width: "95%",
          margin: "0 auto",
          borderCollapse: "collapse",
          background: "transparent",
          tableLayout: "fixed"
        }}
      >
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Supplier ID</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map(product =>
            <tr key={product.id}>
              <td style={{ whiteSpace: "nowrap", textAlign: "center" }}>{product.name}</td>
              <td style={{ whiteSpace: "nowrap", textAlign: "center" }}>{product.price}</td>
              <td style={{ whiteSpace: "nowrap", textAlign: "center" }}>{product.stock}</td>
              <td style={{ whiteSpace: "nowrap", textAlign: "center" }}>{product.supplierId || ""}</td>
              <td style={{ whiteSpace: "nowrap", textAlign: "center" }}>{product.category || ""}</td>
              <td style={{ textAlign: "center" }}>
                <button
                  onClick={() => handleEditProduct(product)}
                  style={{
                    background: "transparent",
                    border: "1px solid #000000ff",
                    color: "#090306ff",
                    borderRadius: "6px",
                    padding: "6px 16px",
                    cursor: "pointer"
                  }}
                >
                  Edit
                </button>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
