import React, { useEffect, useState } from "react";
import { getSupplierOrders, updateOrderStatus } from "../../api";

export default function SupplierOrders() {
  const [orders, setOrders] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");
  const [statusUpdating, setStatusUpdating] = useState({}); // { [orderId]: true/false }

  useEffect(() => {
    loadOrders();
    // eslint-disable-next-line
  }, []);

  const loadOrders = async () => {
    setErrorMsg("");
    try {
      const data = await getSupplierOrders(); // Should fetch orders for the logged-in supplier
      setOrders(data);
    } catch {
      setErrorMsg("Failed to load orders.");
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    setStatusUpdating(s => ({ ...s, [orderId]: true }));
    setErrorMsg("");
    try {
      await updateOrderStatus(orderId, newStatus);
      setOrders(orders =>
        orders.map(order =>
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
    } catch {
      setErrorMsg("Failed to update order status.");
    }
    setStatusUpdating(s => ({ ...s, [orderId]: false }));
  };

  return (
    <div>
      <h2>My Orders</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Product(s)</th>
            <th>Quantity</th>
            <th>Status</th>
            <th>Update Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.length === 0 && (
            <tr>
              <td colSpan={5}>No orders found.</td>
            </tr>
          )}
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>
                {order.products
                  ? order.products.map(p => p.name).join(", ")
                  : "-"}
              </td>
              <td>
                {order.products
                  ? order.products.map(p => p.quantity).join(", ")
                  : "-"}
              </td>
              <td>{order.status}</td>
              <td>
                <select
                  value={order.status}
                  disabled={statusUpdating[order.id]}
                  onChange={e => handleStatusChange(order.id, e.target.value)}
                >
                  <option value="PENDING">PENDING</option>
                  <option value="PROCESSING">PROCESSING</option>
                  <option value="SHIPPED">SHIPPED</option>
                  <option value="DELIVERED">DELIVERED</option>
                  <option value="CANCELLED">CANCELLED</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
