import React, { useState } from "react";
import "./UserRegister.css";

export default function UserRegister() {
  const [form, setForm] = useState({
    username: "",
    name: "",
    email: "",
    password: "",
    role: "CUSTOMER",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const res = await fetch("/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (!res.ok) {
        let errMsg = `Registration failed: ${res.status} ${res.statusText}`;
        try {
          const errJson = await res.json();
          if (errJson.message) errMsg = errJson.message;
          else if (errJson.error) errMsg = errJson.error;
        } catch {
          // fallback to text
          const errText = await res.text();
          if (errText) errMsg = errText;
        }
        setMessage(errMsg);
        return;
      }
      setMessage("Registration successful!");
      setForm({ username: "", name: "", email: "", password: "", role: "CUSTOMER" });
    } catch (err) {
      setMessage("Registration failed: network error.");
    }
  };

  return (
    <form className="user-register-form" onSubmit={handleSubmit}>
      <label htmlFor="username">Username</label>
      <input
        id="username"
        name="username"
        type="text"
        placeholder="Username"
        autoComplete="username"
        value={form.username}
        onChange={handleChange}
        required
      />

      <label htmlFor="name">Name</label>
      <input
        id="name"
        name="name"
        type="text"
        placeholder="Name"
        autoComplete="name"
        value={form.name}
        onChange={handleChange}
        required
      />

      <label htmlFor="email">Email</label>
      <input
        id="email"
        name="email"
        type="email"
        placeholder="Email"
        autoComplete="email"
        value={form.email}
        onChange={handleChange}
        required
      />

      <label htmlFor="password">Password</label>
      <input
        id="password"
        name="password"
        type="password"
        placeholder="Password"
        autoComplete="new-password"
        value={form.password}
        onChange={handleChange}
        required
      />

      <label htmlFor="role">Role</label>
      <select
        id="role"
        name="role"
        autoComplete="off"
        value={form.role}
        onChange={handleChange}
      >
        <option value="CUSTOMER">Customer</option>
        <option value="MANAGER">Manager</option>
        <option value="STAFF">Staff</option>
        <option value="SUPPLIER">Supplier</option>
        <option value="ADMIN">Admin</option>
      </select>

      <button type="submit">Register</button>
      {message && <div>{message}</div>}
    </form>
  );
}
