import React, { useEffect, useState } from "react";
import axios from "axios";

const API_BASE = "https://opulent-happiness-69v4w4jj7xggf4g5w-8080.app.github.dev";

export default function Verification() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`${API_BASE}/pending-verifications`, {
      headers: {
        Authorization: "Basic " + btoa("admin:adminpwd"),
      },
    })
      .then(res => setUsers(res.data))
      .finally(() => setLoading(false));
  }, []);

  const handleVerify = async (id) => {
    await axios.post(`${API_BASE}/verify/${id}`, {}, {
      headers: {
        Authorization: "Basic " + btoa("admin:adminpwd"),
      },
    });
    setUsers(users =>
      users.map(u =>
        u.id === id ? { ...u, verified: true } : u
      )
    );
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2>Pending Verifications</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Verification</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>
                {u.verified
                  ? <span>Verified</span>
                  : <button onClick={() => handleVerify(u.id)}>Verify</button>
                }
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
