import React, { useEffect, useState } from "react";
import { getUsers } from "../../api";
import "./SupplierAdd.css";

export default function SupplierAdd({ supplier, onAdd, onCancel, isEdit }) {
  const [form, setForm] = useState({
    company
  });
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    getUsers().then(data => setUsers(data.filter(u => u.role === "SUPPLIER")));
  }, []);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    const payload = {
      ...form,
      user: form.userId ? { id: Number(form.userId) } : undefined
    };
    try {
      await onAdd(payload);
      setMsg("");
    } catch {
      setMsg(isEdit ? "Failed to update supplier." : "Failed to add supplier.");
    }
  };

  return (
    <form onSubmit={onSubmit} className="supplier-add-form">
      <label>
        Company Name
        <input name="companyName" value={form.companyName} onChange={onChange} required />
      </label>
      <label>
        Email
        <input name="email" type="email" value={form.email} onChange={onChange} required />
      </label>
      <label>
        Phone
        <input name="phone" value={form.phone} onChange={onChange} required />
      </label>
      <label>
        Address
        <input name="address" value={form.address} onChange={onChange} required />
      </label>
      <label>
        Select Supplier User
        <select name="userId" value={form.userId} onChange={onChange} required>
          <option value="">Select Supplier User</option>
          {users.map(u => (
            <option key={u.id} value={u.id}>
              {u.username} ({u.email}) [ID: {u.id}]
            </option>
          ))}
        </select>
      </label>
      <div className="form-actions">
        <button type="submit">{isEdit ? "Save" : "Add Supplier"}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
      {msg && <div style={{ color: "red" }}>{msg}</div>}
    </form>
  );
}
