import React, { useEffect, useState } from "react";
import ProductList from "../../components/Product/ProductList";
import ProductAdd from "../../components/Product/ProductAdd";
import ProductEdit from "../../components/Product/ProductEdit";
import ProductDelete from "../../components/Product/ProductDelete";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff", padding: 24, borderRadius: 8, minWidth: 400, boxShadow: "0 2px 16px rgba(0,0,0,0.15)", position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function ProductPage() {
  const [products, setProducts] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [editProduct, setEditProduct] = useState(null);
  const [deleteProduct, setDeleteProduct] = useState(null);

  // Fetch all products
  useEffect(() => {
    fetch("/api/products")
      .then(res => res.json())
      .then(setProducts)
      .catch(() => setProducts([]));
  }, []);

  // Add product
  const handleAdd = async (data) => {
    try {
      const res = await fetch("/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!res.ok) throw new Error("Add failed");
      const prod = await res.json();
      setProducts(p => [...p, prod]);
      setShowAdd(false);
    } catch {
      alert("Failed to add product");
    }
  };

  // Edit product
  const handleEdit = async (id, data) => {
    try {
      const res = await fetch(`/api/products/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      });
      if (!res.ok) throw new Error("Edit failed");
      const prod = await res.json();
      setProducts(p => p.map(pr => pr.id === prod.id ? prod : pr));
      setEditProduct(null);
    } catch {
      alert("Failed to update product");
    }
  };

  // Delete product
  const handleDelete = async (id) => {
    try {
      const res = await fetch(`/api/products/${id}`, { method: "DELETE" });
      if (!res.ok) {
        const errorBody = await res.json();
        throw new Error(errorBody.message || "Delete failed");
      }
      setProducts(p => p.filter(pr => pr.id !== id));
      setDeleteProduct(null);
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div>
      <h2>Product Management</h2>
      <nav style={{ marginBottom: 12 }}>
        <button onClick={() => setShowAdd(true)}>Add Product</button>
      </nav>

      <ProductList
        products={products}
        onEdit={prod => setEditProduct(prod)}
        onDelete={prod => setDeleteProduct(prod)}
      />

      <Modal open={showAdd} onClose={() => setShowAdd(false)}>
        <ProductAdd onAdd={handleAdd} onCancel={() => setShowAdd(false)} />
      </Modal>
      <Modal open={!!editProduct} onClose={() => setEditProduct(null)}>
        {editProduct && (
          <ProductEdit
            product={editProduct}
            onEdit={data => handleEdit(editProduct.id, data)}
            onCancel={() => setEditProduct(null)}
          />
        )}
      </Modal>
      <Modal open={!!deleteProduct} onClose={() => setDeleteProduct(null)}>
        {deleteProduct && (
          <ProductDelete
            product={deleteProduct}
            onDelete={() => handleDelete(deleteProduct.id)}
            onCancel={() => setDeleteProduct(null)}
          />
        )}
      </Modal>
      <pre>{JSON.stringify(products, null, 2)}</pre>
    </div>
  );
}
