import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";

// Auth pages
import Login from "./pages/auth/Login";
import Register from "./pages/auth/Register";

// Admin pages
import AdminDashboard from "./pages/admin/AdminDashboard";
import Verification from "./pages/admin/Verification";
import UsersPage from "./pages/admin/UsersPage";
import ProductPage from "./pages/admin/ProductPage";
import SupplierPage from "./pages/admin/SupplierPage";
import OrderPage from "./pages/admin/OrderPage";

// Manager pages
import ManagerDashboard from "./pages/manager/ManagerDashboard";
import ManagerOrders from "./pages/manager/ManagerOrders";
import ManagerProducts from "./pages/manager/ManagerProducts";
import ManagerSuppliers from "./pages/manager/ManagerSuppliers";

// Supplier pages
import SupplierDashboard from "./pages/supplier/SupplierDashboard";
import SupplierProfile from "./pages/supplier/SupplierProfile";
import SupplierOrders from "./pages/supplier/SupplierOrders";

// Staff pages
import StaffDashboard from "./pages/staff/StaffDashboard";
import StaffOrders from "./pages/staff/StaffOrders";
import StaffProducts from "./pages/staff/StaffProducts";
import StaffSuppliers from "./pages/staff/StaffSuppliers";
import StaffProfile from "./pages/staff/StaffProfile";

// Customer pages
import CustomerDashboard from "./pages/customer/CustomerDashboard";

// Route guards
import RoleRoute from "./routes/RoleRoute";
import { Auth } from "./auth";

function AppNav() {
  const navigate = useNavigate();
  const location = useLocation();

  const isLoggedIn = Auth.isAuthenticated();
  const inAdminArea = location.pathname.startsWith("/admin");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("auth");
    localStorage.removeItem("role");
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("auth");
    sessionStorage.removeItem("role");
    navigate("/login", { replace: true });
  };

  return (
    <nav style={{ padding: 12, display: "flex", justifyContent: "space-between", alignItems: "center" }}>
      <div>
        {!isLoggedIn && !inAdminArea && (
          <>
            <Link to="/login">Login</Link>{" | "}
            <Link to="/register">Register</Link>
          </>
        )}
      </div>
      <div>
        {inAdminArea && isLoggedIn && (
          <button onClick={handleLogout}>Logout</button>
        )}
      </div>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <AppNav />
      <Routes>
        {/* Auth routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Admin nested routes */}
        <Route
          path="/admin"
          element={
            <RoleRoute allow={["ADMIN"]}>
              <AdminDashboard />
            </RoleRoute>
          }
        >
          <Route index element={<div>Welcome, Admin</div>} />
          <Route path="dashboard" element={<div>Welcome, Admin</div>} />
          <Route path="verification" element={<Verification />} />
          <Route path="users/*" element={<UsersPage />} />
          <Route path="products" element={<ProductPage />} />
          <Route path="suppliers" element={<SupplierPage />} />
          <Route path="orders/*" element={<OrderPage />} />
        </Route>

        {/* Manager nested routes */}
        <Route
          path="/manager"
          element={
            <RoleRoute allow={["MANAGER"]}>
              <ManagerDashboard />
            </RoleRoute>
          }
        >
          <Route index element={<div>Welcome, Manager</div>} />
          <Route path="dashboard" element={<div>Welcome, Manager</div>} />
          <Route path="orders" element={<ManagerOrders />} />
          <Route path="products" element={<ManagerProducts />} />
          <Route path="suppliers" element={<ManagerSuppliers />} />
        </Route>

        {/* Supplier nested routes */}
        <Route
          path="/supplier"
          element={
            <RoleRoute allow={["SUPPLIER"]}>
              <SupplierDashboard />
            </RoleRoute>
          }
        >
          <Route index element={<div>Welcome, Supplier</div>} />
          <Route path="dashboard" element={<div>Welcome, Supplier</div>} />
          <Route path="profile" element={<SupplierProfile />} />
          <Route path="orders" element={<SupplierOrders />} />
        </Route>

        {/* Staff nested routes */}
        <Route
          path="/staff"
          element={
            <RoleRoute allow={["STAFF"]}>
              <StaffDashboard />
            </RoleRoute>
          }
        >
          <Route index element={<div>Welcome, Staff</div>} />
          <Route path="dashboard" element={<div>Welcome, Staff</div>} />
          <Route path="orders" element={<StaffOrders />} />
          <Route path="products" element={<StaffProducts />} />
          <Route path="suppliers" element={<StaffSuppliers />} />
          <Route path="profile" element={<StaffProfile />} />
        </Route>

        {/* Customer nested routes */}
        <Route
          path="/customer"
          element={
            <RoleRoute allow={["CUSTOMER"]}>
              <CustomerDashboard />
            </RoleRoute>
          }
        >
          <Route index element={<div>Welcome, Customer</div>} />
          <Route path="dashboard" element={<div>Welcome, Customer</div>} />
        </Route>

        {/* Fallback */}
        <Route path="*" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
