import React, { useEffect, useState } from "react";
import { getProducts, addProduct, updateProduct } from "../../api";

export default function ManagerProducts() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [addForm, setAddForm] = useState({ name: "", price: "", stock: "", supplierId: "" });
  const [errorMsg, setErrorMsg] = useState(""); // Add this state

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch {
      // Handle error as needed
    }
  };

  const handleSearch = e => setSearch(e.target.value);

  const filteredProducts = products.filter(
    p => p.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleEditProduct = product => {
    setEditingProduct(product);
    setAddForm({
      name: product.name,
      price: product.price,
      stock: product.stock,
      supplierId: product.supplierId || ""
    });
  };

  const handleUpdateProduct = async () => {
    setErrorMsg(""); // Clear previous error
    try {
      await updateProduct(editingProduct.id, {
        ...addForm,
        price: Number(addForm.price),
        stock: Number(addForm.stock),
        supplierId: Number(addForm.supplierId)
      });
      setEditingProduct(null);
      setAddForm({ name: "", price: "", stock: "", supplierId: "" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to update product");
    }
  };

  const handleAddProduct = async e => {
    e.preventDefault();
    setErrorMsg(""); // Clear previous error
    try {
      await addProduct({
        ...addForm,
        price: Number(addForm.price),
        stock: Number(addForm.stock),
        supplierId: Number(addForm.supplierId)
      });
      setShowAddForm(false);
      setAddForm({ name: "", price: "", stock: "", supplierId: "" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to add product");
    }
  };

  return (
    <div>
      <h2>Products</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <div style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={handleSearch}
          style={{ padding: 6, width: 220 }}
        />
        <button onClick={() => setShowAddForm(!showAddForm)} style={{ marginLeft: 16 }}>
          {showAddForm ? "Cancel" : "Add Product"}
        </button>
      </div>

      {showAddForm && (
        <form onSubmit={handleAddProduct} style={{ marginBottom: 24 }}>
          <input
            required
            placeholder="Name"
            value={addForm.name}
            onChange={e => setAddForm(f => ({ ...f, name: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Price"
            value={addForm.price}
            onChange={e => setAddForm(f => ({ ...f, price: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Stock"
            value={addForm.stock}
            onChange={e => setAddForm(f => ({ ...f, stock: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Supplier ID"
            value={addForm.supplierId}
            onChange={e => setAddForm(f => ({ ...f, supplierId: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <button type="submit">Add</button>
        </form>
      )}

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Supplier ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map(product =>
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>{product.supplierId || ""}</td>
              <td>
                {editingProduct && editingProduct.id === product.id ? (
                  <>
                    <input
                      type="text"
                      value={addForm.name}
                      onChange={e => setAddForm(f => ({ ...f, name: e.target.value }))}
                      placeholder="Name"
                      style={{ width: 80, marginRight: 4 }}
                    />
                    <input
                      type="number"
                      value={addForm.price}
                      onChange={e => setAddForm(f => ({ ...f, price: e.target.value }))}
                      placeholder="Price"
                      style={{ width: 60, marginRight: 4 }}
                    />
                    <input
                      type="number"
                      value={addForm.stock}
                      onChange={e => setAddForm(f => ({ ...f, stock: e.target.value }))}
                      placeholder="Stock"
                      style={{ width: 60, marginRight: 4 }}
                    />
                    <input
                      type="number"
                      value={addForm.supplierId}
                      onChange={e => setAddForm(f => ({ ...f, supplierId: e.target.value }))}
                      placeholder="Supplier ID"
                      style={{ width: 80, marginRight: 4 }}
                    />
                    <button onClick={handleUpdateProduct}>Save</button>
                    <button onClick={() => setEditingProduct(null)} style={{ marginLeft: 4 }}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEditProduct(product)}>Edit</button>
                )}
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
