import React, { useState } from "react";
import { registerUser } from "../../api";
import { Link, useNavigate } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    confirm: "",
    role: "CUSTOMER"
  });
  const [msg, setMsg] = useState("");
  const nav = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    if (form.password !== form.confirm) {
      setMsg("Passwords do not match");
      return;
    }
    try {
      await registerUser({
        name: form.name,
        username: form.username,
        email: form.email,
        password: form.password,
        role: form.role
      });
      setMsg("Registered successfully!");
      nav("/login");
    } catch {
      setMsg("Registration failed.");
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Create Account</h2>
      <form onSubmit={onSubmit}>
        <input name="name" placeholder="Name" value={form.name} onChange={onChange} required /><br />
        <input name="username" placeholder="Username" value={form.username} onChange={onChange} required /><br />
        <input name="email" type="email" placeholder="Email" value={form.email} onChange={onChange} required /><br />
        <input name="password" type="password" placeholder="Password" value={form.password} onChange={onChange} required /><br />
        <input name="confirm" type="password" placeholder="Confirm Password" value={form.confirm} onChange={onChange} required /><br />
        <select name="role" value={form.role} onChange={onChange}>
          <option value="CUSTOMER">Customer</option>
          <option value="MANAGER">Manager</option>
          <option value="SUPPLIER">Supplier</option>
          <option value="STAFF">Staff</option>
          <option value="ADMIN">Admin</option>
        </select>
        <br />
        <button type="submit">Register</button>
      </form>
      <div>{msg}</div>
      <div>
        Already have an account? <Link to="/login">Login</Link>
      </div>
    </div>
  );
}
