import React, { useEffect, useState } from "react";
import { getSuppliers, addSupplier, updateSupplier } from "../../api";

export default function ManagerSuppliers() {
  const [suppliers, setSuppliers] = useState([]);
  const [search, setSearch] = useState("");
  const [editingSupplier, setEditingSupplier] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [form, setForm] = useState({ companyName: "", email: "", phone: "", address: "" });
  const [errorMsg, setErrorMsg] = useState("");

  useEffect(() => {
    loadSuppliers();
  }, []);

  const loadSuppliers = async () => {
    try {
      const data = await getSuppliers();
      setSuppliers(data);
    } catch {
      setErrorMsg("Failed to load suppliers");
    }
  };

  const handleSearch = e => setSearch(e.target.value);

  const filteredSuppliers = suppliers.filter(
    s => (s.companyName || "").toLowerCase().includes(search.toLowerCase())
  );

  const handleEditSupplier = supplier => {
    setEditingSupplier(supplier);
    setForm({
      companyName: supplier.companyName || "",
      email: supplier.email || "",
      phone: supplier.phone || "",
      address: supplier.address || ""
    });
  };

  const handleUpdateSupplier = async () => {
    setErrorMsg("");
    try {
      await updateSupplier(editingSupplier.id, form);
      setEditingSupplier(null);
      setForm({ companyName: "", email: "", phone: "", address: "" });
      loadSuppliers();
    } catch (err) {
      setErrorMsg(err.message || "Failed to update supplier");
    }
  };

  const handleAddSupplier = async e => {
    e.preventDefault();
    setErrorMsg("");
    try {
      await addSupplier(form);
      setShowAddForm(false);
      setForm({ companyName: "", email: "", phone: "", address: "" });
      loadSuppliers();
    } catch (err) {
      setErrorMsg(err.message || "Failed to add supplier");
    }
  };

  return (
    <div>
      <h2>Suppliers</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <div style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by company name"
          value={search}
          onChange={handleSearch}
          style={{ padding: 6, width: 220 }}
        />
        <button onClick={() => setShowAddForm(!showAddForm)} style={{ marginLeft: 16 }}>
          {showAddForm ? "Cancel" : "Add Supplier"}
        </button>
      </div>

      {showAddForm && (
        <form onSubmit={handleAddSupplier} style={{ marginBottom: 24 }}>
          <input
            required
            placeholder="Company Name"
            value={form.companyName}
            onChange={e => setForm(f => ({ ...f, companyName: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={e => setForm(f => ({ ...f, email: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            placeholder="Phone"
            value={form.phone}
            onChange={e => setForm(f => ({ ...f, phone: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            placeholder="Address"
            value={form.address}
            onChange={e => setForm(f => ({ ...f, address: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <button type="submit">Add</button>
        </form>
      )}

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredSuppliers.map(supplier =>
            <tr key={supplier.id}>
              <td>{supplier.companyName}</td>
              <td>{supplier.email || ""}</td>
              <td>{supplier.phone || ""}</td>
              <td>{supplier.address || ""}</td>
              <td>
                {editingSupplier && editingSupplier.id === supplier.id ? (
                  <>
                    <input
                      type="text"
                      value={form.companyName}
                      onChange={e => setForm(f => ({ ...f, companyName: e.target.value }))}
                      placeholder="Company Name"
                      style={{ width: 120, marginRight: 4 }}
                    />
                    <input
                      type="email"
                      value={form.email}
                      onChange={e => setForm(f => ({ ...f, email: e.target.value }))}
                      placeholder="Email"
                      style={{ width: 120, marginRight: 4 }}
                    />
                    <input
                      type="text"
                      value={form.phone}
                      onChange={e => setForm(f => ({ ...f, phone: e.target.value }))}
                      placeholder="Phone"
                      style={{ width: 100, marginRight: 4 }}
                    />
                    <input
                      type="text"
                      value={form.address}
                      onChange={e => setForm(f => ({ ...f, address: e.target.value }))}
                      placeholder="Address"
                      style={{ width: 140, marginRight: 4 }}
                    />
                    <button onClick={handleUpdateSupplier}>Save</button>
                    <button onClick={() => setEditingSupplier(null)} style={{ marginLeft: 4 }}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEditSupplier(supplier)}>Edit</button>
                )}
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
