import React, { useState, useEffect } from "react";
import { addSupplier, updateSupplier } from "../../api";

export default function SupplierAdd({ onAdd, onCancel, supplier, isEdit }) {
  const [form, setForm] = useState({
    supplierId: "",
    companyName: "",
    email: "",
    phone: "",
    address: ""
  });
  const [msg, setMsg] = useState("");

  // Reset form and message when supplier or isEdit changes (modal opens)
  useEffect(() => {
    if (supplier) {
      setForm({
        supplierId: supplier.supplierId || "",
        companyName: supplier.companyName || "",
        email: supplier.email || "",
        phone: supplier.phone || "",
        address: supplier.address || ""
      });
    } else {
      setForm({
        supplierId: "",
        companyName: "",
        email: "",
        phone: "",
        address: ""
      });
    }
    setMsg(""); // Clear any previous message when modal opens
  }, [supplier, isEdit]);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      if (isEdit) {
        await updateSupplier(supplier.id, form); // assuming backend still expects db `id`
        setMsg("Supplier updated");
      } else {
        await addSupplier(form);
        setMsg("Supplier added");
        setForm({ supplierId: "", companyName: "", email: "", phone: "", address: "" });
      }
      if (onAdd) onAdd(form);
    } catch (err) {
      // Show a friendly error if supplier already exists (409)
      if (err.message && err.message.startsWith("409")) {
        setMsg("A supplier with this ID, company name or email already exists.");
      } else {
        setMsg(isEdit ? "Supplier update failed. Please try again." : "Failed to add supplier. Please try again.");
      }
    }
  };

  return (
    <form
      onSubmit={onSubmit}
      style={{ display: "flex", flexDirection: "column", gap: 12, minWidth: 300 }}
    >
      <input
        name="supplierId"
        placeholder="Supplier ID"
        value={form.supplierId}
        onChange={onChange}
        required
        disabled={isEdit} // prevent changing supplierId during edit
      />
      <input
        name="companyName"
        placeholder="Company Name"
        value={form.companyName}
        onChange={onChange}
        required
      />
      <input
        name="email"
        type="email"
        placeholder="Email"
        value={form.email}
        onChange={onChange}
        required
      />
      <input
        name="phone"
        placeholder="Phone"
        value={form.phone}
        onChange={onChange}
        required
      />
      <input
        name="address"
        placeholder="Address"
        value={form.address}
        onChange={onChange}
        required
      />
      <div style={{ display: "flex", gap: 8 }}>
        <button type="submit">{isEdit ? "Save" : "Add Supplier"}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
      <div style={{ color: msg.includes("added") || msg.includes("updated") ? "green" : "red" }}>
        {msg}
      </div>
    </form>
  );
}
