import React, { useEffect, useState } from "react";
// Add this import if you have an API utility
import { placeOrder } from "../../api"; // Adjust path as needed

export default function CustomerCart() {
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem("cart");
    return saved ? JSON.parse(saved) : [];
  });
  const [message, setMessage] = useState("");

  useEffect(() => {
    const syncCart = () => {
      const saved = localStorage.getItem("cart");
      setCart(saved ? JSON.parse(saved) : []);
    };
    window.addEventListener("storage", syncCart);
    return () => window.removeEventListener("storage", syncCart);
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const removeFromCart = (id) => {
    const updated = cart.filter(item => item.id !== id);
    setCart(updated);
  };

  const clearCart = () => {
    setCart([]);
    localStorage.removeItem("cart");
  };

  const incrementQty = (id) => {
    setCart(cart =>
      cart.map(item =>
        item.id === id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      )
    );
  };

  const decrementQty = (id) => {
    setCart(cart =>
      cart
        .map(item =>
          item.id === id && item.quantity > 1
            ? { ...item, quantity: item.quantity - 1 }
            : item
        )
        .filter(item => item.quantity > 0)
    );
  };

  const handlePlaceOrder = async () => {
    try {
      // Replace with your actual API call
      await placeOrder(cart);
      setCart([]);
      localStorage.removeItem("cart");
      setMessage("Order placed successfully!");
    } catch (err) {
      setMessage("Failed to place order. Please try again.");
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {message && <p>{message}</p>}
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
            <thead>
              <tr>
                <th>Name</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Remove</th>
              </tr>
            </thead>
            <tbody>
              {cart.map(item => (
                <tr key={item.id}>
                  <td>{item.name}</td>
                  <td>
                    <button onClick={() => decrementQty(item.id)} disabled={item.quantity <= 1}>-</button>
                    <span style={{ margin: "0 8px" }}>{item.quantity}</span>
                    <button onClick={() => incrementQty(item.id)}>+</button>
                  </td>
                  <td>{item.price}</td>
                  <td>
                    <button onClick={() => removeFromCart(item.id)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={clearCart} style={{ marginTop: 16, marginRight: 8 }}>Clear Cart</button>
          <button onClick={handlePlaceOrder} style={{ marginTop: 16 }}>Place Order</button>
        </>
      )}
    </div>
  );
}
