import React, { useEffect, useState } from "react";
import { getSuppliers, addSupplier, updateSupplier, deleteSupplier, getUsers, assignSupplierToOrder } from "../../api";
import SupplierAdd from "../../components/Supplier/SupplierAdd";
import SupplierList from "../../components/Supplier/SupplierList";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function SupplierPage() {
  const [suppliers, setSuppliers] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [editSupplier, setEditSupplier] = useState(null);
  const [showList, setShowList] = useState(false);
  const [search, setSearch] = useState("");
  const [editSupplierId, setEditSupplierId] = useState(null);
  const [newUserId, setNewUserId] = useState("");

  useEffect(() => {
    const fetchSuppliers = async () => {
      const data = await getSuppliers(); // data is an array of supplier objects
      // Map userId from user object if needed
      const suppliersWithUserId = data.map(s => ({
        ...s,
        userId: s.userId || (s.user && s.user.id) || ""
      }));
      setSuppliers(suppliersWithUserId);
    };

    fetchSuppliers().catch(() => setSuppliers([]));
  }, []);

  const handleAdd = async (data) => {
    try {
      const supplier = await addSupplier(data);
      setSuppliers(s => [...s, supplier]);
      setShowAdd(false);
    } catch {
      // Do nothing here; error will be handled in the modal
    }
  };

  const handleEdit = async (id, data) => {
    try {
      const updated = await updateSupplier(id, data);
      setSuppliers(s => s.map(sup => sup.id === id ? updated : sup));
      setEditSupplier(null);
    } catch {
      // Do nothing here; error will be handled in the modal
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteSupplier(id);
      setSuppliers(s => s.filter(sup => sup.id !== id));
    } catch {
      // Optionally handle delete errors in the future
    }
  };

  const handleUserIdChange = (supplierId, value) => {
    setEditSupplierId(supplierId);
    setNewUserId(value);
  };

  const handleSaveUserId = async (supplierId) => {
    try {
      await updateSupplier(supplierId, { user: { id: Number(newUserId) } });
      setSuppliers(suppliers =>
        suppliers.map(s =>
          s.id === supplierId ? { ...s, user: { id: Number(newUserId) } } : s
        )
      );
      setEditSupplierId(null);
      setNewUserId("");
    } catch {
      alert("Failed to update Supplier ID.");
    }
  };

  const handleAssignSupplier = async (orderId, supplierUserId) => {
    try {
      await assignSupplierToOrder(orderId, supplierUserId); // pass userId!
      // Optionally refresh orders or show success
    } catch (e) {
      alert("Failed to assign supplier");
    }
  };

  const filteredSuppliers = suppliers.filter(s =>
    s.companyName?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Supplier Management</h2>
      <button onClick={() => setShowAdd(true)}>Add Supplier</button>
      <button style={{ marginLeft: 8 }} onClick={() => setShowList(true)}>
        View Suppliers
      </button>
      <div style={{ margin: "12px 0" }}>
        <input
          type="text"
          placeholder="Search supplier by company name"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <button onClick={() => setSearch(search)}>Search</button>
      </div>
      <table
        border="1"
        cellPadding="8"
        style={{
          width: "70%",
          minWidth: 600,
          maxWidth: 900,
          margin: "0 auto",
          background: "#fff",
          borderRadius: 8
        }}
      >
        <thead>
          <tr>
            <th>Supplier ID</th>
            <th>Company Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>User ID</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {filteredSuppliers.map(s => (
            <tr key={s.id}>
              <td>{s.id}</td>
              <td>{s.companyName}</td>
              <td>{s.email}</td>
              <td>{s.phone}</td>
              <td>{s.address}</td>
              <td>{s.userId || ""}</td>
              <td>
                <button onClick={() => setEditSupplier(s)}>Edit</button>
              </td>
              <td>
                <button onClick={() => handleDelete(s.id)}>Delete</button>
              </td>
            </tr>
          ))}
          {filteredSuppliers.length === 0 && (
            <tr><td colSpan={8}>No suppliers found</td></tr>
          )}
        </tbody>
      </table>

      {/* View Suppliers Modal */}
      <Modal open={showList} onClose={() => setShowList(false)}>
        <SupplierList />
      </Modal>

      {/* Add Supplier Modal */}
      <Modal open={showAdd} onClose={() => setShowAdd(false)}>
        <SupplierAdd
          key={showAdd ? Date.now() : undefined}
          onAdd={handleAdd}
          onCancel={() => setShowAdd(false)}
        />
      </Modal>

      {/* Edit Supplier Modal */}
      <Modal open={!!editSupplier} onClose={() => setEditSupplier(null)}>
        {editSupplier && (
          <SupplierAdd
            supplier={editSupplier}
            onAdd={(data) => handleEdit(editSupplier.id, data)}
            onCancel={() => setEditSupplier(null)}
            isEdit
          />
        )}
      </Modal>
    </div>
  );
}

export function SupplierEdit({ supplierId, initial, onSave, onCancel }) {
  const [form, setForm] = useState(
    initial || { companyName: "", email: "", phone: "", address: "", userId: "" }
  );
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    getUsers().then(data => setUsers(data.filter(u => u.role === "SUPPLIER")));
  }, []);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onUserIdChange = e => setForm({ ...form, userId: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    const payload = {
      ...form,
      user: form.userId ? { id: Number(form.userId) } : undefined
    };
    try {
      await onSave(payload);
      setMsg("Supplier updated");
    } catch {
      setMsg("Failed to update supplier");
    }
  };

  return (
    <form onSubmit={onSubmit} style={{ display: "flex", flexDirection: "column", gap: 12, minWidth: 300 }}>
      <input name="companyName" placeholder="Company Name" value={form.companyName} onChange={onChange} required />
      <input name="email" type="email" placeholder="Email" value={form.email} onChange={onChange} required />
      <input name="phone" placeholder="Phone" value={form.phone} onChange={onChange} required />
      <input name="address" placeholder="Address" value={form.address} onChange={onChange} required />
      <div>
        <label>User ID: </label>
        <input
          name="userId"
          type="number"
          placeholder="User ID"
          value={form.userId || ""}
          onChange={onUserIdChange}
          style={{ width: 120, marginRight: 8 }}
        />
        <select
          value={form.userId || ""}
          onChange={onUserIdChange}
          style={{ width: 220 }}
        >
          <option value="">Select Supplier User</option>
          {users.map(u => (
            <option key={u.id} value={u.id}>
              {u.username} ({u.email}) [ID: {u.id}]
            </option>
          ))}
        </select>
      </div>
      <div style={{ display: "flex", gap: 8 }}>
        <button type="submit">Save</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
      <div style={{ color: msg ? "red" : undefined }}>
        {msg}
      </div>
    </form>
  );
}
