import React, { useEffect, useState } from "react";
import { getProducts, updateProduct } from "../../api";

export default function StaffProducts() {
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [stock, setStock] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  useEffect(() => {
    getProducts().then(setProducts).catch(() => setErrorMsg("Failed to load products."));
  }, []);

  const handleEdit = (id, currentStock) => {
    setEditingId(id);
    setStock(currentStock);
  };

  const handleSave = async (id) => {
    try {
      const product = products.find(p => p.id === id); // Get the full product object
      await updateProduct(id, { ...product, stock: Number(stock) });
      setProducts(products =>
        products.map(p => p.id === id ? { ...p, stock: Number(stock) } : p)
      );
      setEditingId(null);
      setStock("");
    } catch {
      setErrorMsg("Failed to update stock.");
    }
  };

  return (
    <div>
      <h2>Product List</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Name</th>
            <th>Stock</th>
            <th>Update Stock</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.stock}</td>
              <td>
                {editingId === p.id ? (
                  <>
                    <input
                      type="number"
                      value={stock}
                      onChange={e => setStock(e.target.value)}
                      style={{ width: 60 }}
                    />
                    <button onClick={() => handleSave(p.id)}>Save</button>
                    <button onClick={() => setEditingId(null)}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEdit(p.id, p.stock)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
