import React, { useEffect, useState } from "react";
import { getSuppliers, addSupplier, updateSupplier, deleteSupplier } from "../../api";
import SupplierAdd from "../../components/Supplier/SupplierAdd";
import SupplierList from "../../components/Supplier/SupplierList";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>✕</button>
        {children}
      </div>
    </div>
  );
}

export default function ManagerSuppliers() {
  const [suppliers, setSuppliers] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [editSupplier, setEditSupplier] = useState(null);
  const [showList, setShowList] = useState(false);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const rowsPerPage = 5;

  useEffect(() => {
    const fetchSuppliers = async () => {
      const data = await getSuppliers();
      const suppliersWithUserId = data.map(s => ({
        ...s,
        userId: s.userId || (s.user && s.user.id) || ""
      }));
      setSuppliers(suppliersWithUserId);
    };
    fetchSuppliers().catch(() => setSuppliers([]));
  }, []);

  const handleAdd = async (data) => {
    try {
      const supplier = await addSupplier(data);
      setSuppliers(s => [...s, supplier]);
      setShowAdd(false);
    } catch {
      // Error handled in modal
    }
  };

  const handleEdit = async (id, data) => {
    try {
      const updated = await updateSupplier(id, data);
      setSuppliers(s => s.map(sup => sup.id === id ? updated : sup));
      setEditSupplier(null);
    } catch {
      // Error handled in modal
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteSupplier(id);
      setSuppliers(s => s.filter(sup => sup.id !== id));
    } catch {
      // Optionally handle delete errors
    }
  };

  const filteredSuppliers = suppliers.filter(s =>
    s.companyName?.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredSuppliers.length / rowsPerPage);
  const paginatedSuppliers = filteredSuppliers.slice(
    (page - 1) * rowsPerPage,
    page * rowsPerPage
  );

  // Reset to first page on search
  useEffect(() => {
    setPage(1);
  }, [search]);

  return (
    <div>
      <h2>Supplier Management</h2>
      <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: 12, marginBottom: 16 }}>
        <button
          onClick={() => setShowAdd(true)}
          style={{
            background: "transparent",
            border: "1px solid #58126A",
            color: "#020102ff",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          Add Supplier
        </button>
        <button
          onClick={() => setShowList(true)}
          style={{
            background: "transparent",
            border: "1px solid #58126A",
            color: "#040105ff",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          View Suppliers
        </button>
        <input
          type="text"
          placeholder="Search supplier by company name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{
            marginLeft: 8,
            background: "transparent",
            border: "1px solid #58126A",
            color: "#58126A",
            padding: "6px 16px",
            borderRadius: "6px"
          }}
        />
        <style>
          {`
            input::placeholder {
              color: black !important;
              opacity: 1;
            }
          `}
        </style>
        <button
          onClick={() => setSearch(search)}
          style={{
            background: "transparent",
            border: "1px solid #58126A",
            color: "#58126A",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          Search
        </button>
      </div>
      <div style={{ width: "100%", overflowX: "auto" }}>
        <table
          border="1"
          cellPadding="8"
          style={{
            width: "70%",
            minWidth: 600,
            maxWidth: 900,
            margin: "0 auto",
            background: "transparent",
            borderRadius: 8
          }}
        >
          <thead style={{ background: "transparent" }}>
            <tr>
              <th>Supplier ID</th>
              <th>Company Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Address</th>
              <th>User ID</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {paginatedSuppliers.map(s => (
              <tr key={s.id} style={{ background: "transparent" }}>
                <td>{s.id}</td>
                <td>{s.companyName}</td>
                <td>{s.email}</td>
                <td>{s.phone}</td>
                <td>{s.address}</td>
                <td>{s.userId || ""}</td>
                <td>
                  <button
                    onClick={() => setEditSupplier(s)}
                    style={{
                      background: "transparent",
                      border: "1px solid #58126A",
                      color: "#58126A",
                      padding: "6px 16px",
                      borderRadius: "6px",
                      cursor: "pointer"
                    }}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  <button
                    onClick={() => handleDelete(s.id)}
                    style={{
                      background: "transparent",
                      border: "1px solid #0a030cff",
                      color: "#58126A",
                      padding: "6px 16px",
                      borderRadius: "6px",
                      cursor: "pointer"
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {paginatedSuppliers.length === 0 && (
              <tr style={{ background: "transparent" }}>
                <td colSpan={8}>No suppliers found</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      {/* Pagination controls */}
      <div style={{ margin: "16px 0", display: "flex", gap: 8, justifyContent: "center" }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>
          Prev
        </button>
        <span>Page {page} of {totalPages || 1}</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages || totalPages === 0}>
          Next
        </button>
      </div>

      {/* View Suppliers Modal */}
      <Modal open={showList} onClose={() => setShowList(false)}>
        <SupplierList />
      </Modal>

      {/* Add Supplier Modal */}
      <Modal open={showAdd} onClose={() => setShowAdd(false)}>
        <SupplierAdd
          key={showAdd ? Date.now() : undefined}
          onAdd={handleAdd}
          onCancel={() => setShowAdd(false)}
        />
      </Modal>

      {/* Edit Supplier Modal */}
      <Modal open={!!editSupplier} onClose={() => setEditSupplier(null)}>
        {editSupplier && (
          <SupplierAdd
            supplier={editSupplier}
            onAdd={(data) => handleEdit(editSupplier.id, data)}
            onCancel={() => setEditSupplier(null)}
            isEdit
          />
        )}
      </Modal>
    </div>
  );
}
