import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { Auth } from "../../auth";

export default function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [msg, setMsg] = useState("");
  const nav = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setMsg("");
    try {
      const basic = btoa(`${form.email}:${form.password}`); // or email if you use email as username
      const res = await fetch("/api/users/me", {
        headers: { Authorization: `Basic ${basic}` },
        credentials: "include"
      });

      if (!res.ok) {
        let errMsg = "Invalid credentials or not verified yet.";
        try {
          const data = await res.json();
          errMsg = data.message || data.error || errMsg;
        } catch {}
        setMsg(errMsg);
        return;
      }

      const user = await res.json(); // expect { id, name, email, role }
      Auth.login(user); // persist user info
      const r = user.role;
      if (r === "ADMIN") nav("/admin/dashboard");
      else if (r === "MANAGER") nav("/manager/dashboard");
      else if (r === "STAFF") nav("/staff/dashboard");
      else if (r === "SUPPLIER") nav("/supplier/dashboard");
      else nav("/customer/dashboard");
    } catch {
      setMsg("Network error. Please try again.");
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Smart Inventory Login</h2>
      <form onSubmit={onSubmit}>
        <input
          name="email"
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={onChange}
          required
        />
        <br />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={onChange}
          required
        />
        <br />
        <button type="submit">Login</button>
      </form>
      <div style={{ color: msg.includes("success") ? "green" : "red" }}>{msg}</div>
      <div>
        Don&apos;t have an account? <Link to="/register">Register</Link>
      </div>
    </div>
  );
}
