import React, { useEffect, useState } from "react";
import { getUsers } from "../../api";
import "./SupplierAdd.css";

export default function SupplierAdd({ supplier, onAdd, onCancel, isEdit }) {
  const [form, setForm] = useState({
    companyName: supplier?.companyName || "",
    email: supplier?.email || "",
    phone: supplier?.phone || "",
    address: supplier?.address || "",
    userId: supplier?.userId || ""
  });
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    getUsers().then(data => setUsers(data.filter(u => u.role === "SUPPLIER")));
  }, []);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    const payload = {
      ...form,
      user: form.userId ? { id: Number(form.userId) } : undefined
    };
    try {
      await onAdd(payload);
      setMsg(isEdit ? "Supplier updated" : "Supplier added");
      setForm({
        companyName: "",
        email: "",
        phone: "",
        address: "",
        userId: ""
      });
    } catch {
      setMsg(isEdit ? "Failed to update supplier." : "Failed to add supplier.");
    }
  };

  return (
    <form className="product-add-form" onSubmit={onSubmit}>
      <input
        name="companyName"
        placeholder="Company Name"
        value={form.companyName}
        onChange={onChange}
        required
      />
      <input
        name="email"
        type="email"
        placeholder="Email"
        value={form.email}
        onChange={onChange}
        required
      />
      <input
        name="phone"
        placeholder="Phone"
        value={form.phone}
        onChange={onChange}
        required
      />
      <input
        name="address"
        placeholder="Address"
        value={form.address}
        onChange={onChange}
        required
      />
      <select
        name="userId"
        value={form.userId}
        onChange={onChange}
        required
      >
        <option value="">Select Supplier User</option>
        {users.map(u => (
          <option key={u.id} value={u.id}>
            {u.username} ({u.email}) [ID: {u.id}]
          </option>
        ))}
      </select>
      <div style={{ display: "flex", gap: 8 }}>
        <button type="submit">{isEdit ? "Save" : "Add Supplier"}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
      <div>{msg}</div>
    </form>
  );
}
