import React from "react";
import { NavLink, Outlet, useNavigate } from "react-router-dom";
import "./admin.css";

export default function AdminDashboard() {
  const navigate = useNavigate();

  // Simple logout handler (customize as needed)
  const handleLogout = () => {
    // Clear tokens/session here if needed
    navigate("/login");
  };

  return (
    <div className="admin-layout">
      {/* Fixed Header Bar */}
      <header className="admin-header-bar" style={{ position: "relative" }}>
        <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
          <h1 className="admin-header-title">Admin</h1>
          <button
            onClick={handleLogout}
            title="Logout"
            style={{
              background: "none",
              border: "none",
              cursor: "pointer",
              marginTop: 4,
              marginLeft: 4,
              padding: 0,
            }}
          >
            {/* Logout SVG icon */}
            <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="#fff" strokeWidth="2"
              strokeLinecap="round" strokeLinejoin="round">
              <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
              <polyline points="16 17 21 12 16 7" />
              <line x1="21" y1="12" x2="9" y2="12" />
            </svg>
          </button>
        </div>
        <nav className="admin-nav">
          <NavLink to="/admin/dashboard">Dashboard</NavLink>
          <NavLink to="/admin/users">Users</NavLink>
          <NavLink to="/admin/products">Products</NavLink>
          <NavLink to="/admin/suppliers">Suppliers</NavLink>
          <NavLink to="/admin/orders">Orders</NavLink>
          <NavLink to="/admin/verification">Verification</NavLink>
        </nav>
      </header>

      {/* Main Card */}
      <main className="admin-card">
        <Outlet />
      </main>
    </div>
  );
}
