import React, { useEffect, useState } from "react";
import { getProducts, addProduct, updateProduct } from "../../api";

export default function ManagerProducts() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [addForm, setAddForm] = useState({ name: "", price: "", stock: "", supplierId: "", category: "" });
  const [errorMsg, setErrorMsg] = useState(""); // Add this state

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch {
      // Handle error as needed
    }
  };

  const handleSearch = e => setSearch(e.target.value);

  const filteredProducts = products.filter(
    p => p.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleEditProduct = product => {
    setEditingProduct(product);
    setAddForm({
      name: product.name,
      price: product.price,
      stock: product.stock,
      supplierId: product.supplierId || "",
      category: product.category || ""
    });
  };

  const handleUpdateProduct = async () => {
    setErrorMsg(""); // Clear previous error
    try {
      await updateProduct(editingProduct.id, {
        ...addForm,
        price: Number(addForm.price),
        stock: Number(addForm.stock),
        supplierId: Number(addForm.supplierId)
      });
      setEditingProduct(null);
      setAddForm({ name: "", price: "", stock: "", supplierId: "", category: "" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to update product");
    }
  };

  const handleAddProduct = async e => {
    e.preventDefault();
    setErrorMsg(""); // Clear previous error
    try {
      await addProduct({
        ...addForm,
        price: Number(addForm.price),
        stock: Number(addForm.stock),
        supplierId: Number(addForm.supplierId)
      });
      setShowAddForm(false);
      setAddForm({ name: "", price: "", stock: "", supplierId: "", category: "" });
      loadProducts();
    } catch (err) {
      setErrorMsg(err.message || "Failed to add product");
    }
  };

  return (
    <div>
      <h2>Products</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <div style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{
            background: "transparent",
            border: "1px solid #FF0078",
            color: "#FF0078",
            padding: "6px 16px",
            borderRadius: "6px",
            marginRight: "12px"
          }}
        />
        <button
          onClick={handleAddProduct}
          style={{
            background: "transparent",
            border: "1px solid #FF0078",
            color: "#FF0078",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          Add Product
        </button>
      </div>

      {showAddForm && (
        <form onSubmit={handleAddProduct} style={{ marginBottom: 24 }}>
          <input
            required
            placeholder="Name"
            value={addForm.name}
            onChange={e => setAddForm(f => ({ ...f, name: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            placeholder="Description"
            value={addForm.description || ""}
            onChange={e => setAddForm(f => ({ ...f, description: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Price"
            value={addForm.price}
            onChange={e => setAddForm(f => ({ ...f, price: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Stock"
            value={addForm.stock}
            onChange={e => setAddForm(f => ({ ...f, stock: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <select
            name="category"
            value={addForm.category || "ELECTRONICS"}
            onChange={e => setAddForm(f => ({ ...f, category: e.target.value }))}
            style={{ marginRight: 8 }}
            required
          >
            <option value="ELECTRONICS">ELECTRONICS</option>
            <option value="GROCERY">GROCERY</option>
            <option value="CLOTHING">CLOTHING</option>
          </select>
          <input
            name="supplierId"
            type="number"
            placeholder="Supplier ID"
            value={addForm.supplierId}
            onChange={e => setAddForm(f => ({ ...f, supplierId: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <button type="submit">Add</button>
        </form>
      )}

      <table
        border="1"
        cellPadding="8"
        style={{
          width: "95%",
          margin: "24px auto 0 auto",
          borderCollapse: "collapse",
          background: "transparent",
          tableLayout: "fixed"
        }}
      >
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Supplier ID</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>{product.supplierId || ""}</td>
              <td>{product.category || ""}</td>
              <td style={{ textAlign: "center" }}>
                <button
                  onClick={() => handleEdit(product)}
                  style={{
                    background: "transparent",
                    border: "1px solid #FF0078",
                    color: "#FF0078",
                    padding: "6px 16px",
                    borderRadius: "6px",
                    cursor: "pointer"
                  }}
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
