import React, { useEffect, useState } from "react";
import { getProducts, deleteProduct, updateProduct } from "../../api";
import ProductAdd from "../../components/Product/ProductAdd";
import ProductEdit from "../../components/Product/ProductEdit";
import ProductDelete from "../../components/Product/ProductDelete";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff", padding: 24, borderRadius: 8, minWidth: 400, boxShadow: "0 2px 16px rgba(0,0,0,0.15)", position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [editing, setEditing] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [deleteProductObj, setDeleteProduct] = useState(null);

  const fetchProducts = () => {
    getProducts()
      .then(setProducts)
      .catch(() => setProducts([]));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleDelete = async (id) => {
    await deleteProduct(id);
    fetchProducts();
  };

  const handleEdit = (product) => setEditing(product);

  const handleEditSave = async (id, updated) => {
    await updateProduct(id, updated);
    setEditing(null);
    fetchProducts();
  };

  return (
    <div>
      <h2>Product Management</h2>
      <button onClick={() => setShowModal(true)}>View Products</button>
      <ProductAdd />
      <h3>All Products</h3>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.price}</td>
              <td>{p.stock}</td>
              <td>{p.category}</td>
              <td>
                <button onClick={() => handleEdit(p)}>Edit</button>
              </td>
              <td>
                <button onClick={() => setDeleteProduct(p)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Modal */}
      <Modal open={!!editing} onClose={() => setEditing(null)}>
        {editing && (
          <ProductEdit
            product={editing}
            onEdit={handleEditSave}
            onCancel={() => setEditing(null)}
          />
        )}
      </Modal>

      {/* View Products Modal */}
      <Modal open={showModal} onClose={() => setShowModal(false)}>
        <h3>Product List</h3>
        <ul>
          {products.map(p => (
            <li key={p.id}>
              {p.name} - ${p.price} - Stock: {p.stock} - {p.category}
            </li>
          ))}
        </ul>
      </Modal>

      {/* Delete Product Modal */}
      <Modal open={!!deleteProductObj} onClose={() => setDeleteProduct(null)}>
        {deleteProductObj && (
          <ProductDelete
            product={deleteProductObj}
            onDelete={() => handleDelete(deleteProductObj.id)}
            onCancel={() => setDeleteProduct(null)}
          />
        )}
      </Modal>
    </div>
  );
}
