import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";

// Auth pages (ensure these files exist or replace with your components)
import Login from "./pages/auth/Login";
import Register from "./pages/auth/Register";

// Admin pages
import AdminDashboard from "./pages/admin/AdminDashboard";
import Verification from "./pages/admin/Verification";

// Manager pages
import ManagerDashboard from "./pages/manager/ManagerDashboard";

// Supplier pages
import SupplierDashboard from "./pages/supplier/SupplierDashboard";

// Staff pages
import StaffDashboard from "./pages/staff/StaffDashboard";

// Customer pages
import CustomerDashboard from "./pages/customer/CustomerDashboard";

// Route guards (create these in frontend/src/routes if you haven’t already)
import RoleRoute from "./routes/RoleRoute";

function AppNav() {
  const navigate = useNavigate();
  const location = useLocation();

  // Read auth from both localStorage and sessionStorage (dev-friendly)
  const role =
    localStorage.getItem("role") ||
    sessionStorage.getItem("role");

  const token =
    localStorage.getItem("token") ||
    sessionStorage.getItem("token") ||
    localStorage.getItem("auth") ||
    sessionStorage.getItem("auth");

  const isLoggedIn = !!token;
  const inAdminArea = location.pathname.startsWith("/admin");

  const handleLogout = () => {
    // Clear auth (both storages)
    localStorage.removeItem("token");
    localStorage.removeItem("auth");
    localStorage.removeItem("role");
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("auth");
    sessionStorage.removeItem("role");
    navigate("/login", { replace: true });
  };

  return (
    <nav style={{ padding: 12, display: "flex", justifyContent: "space-between", alignItems: "center" }}>
      <div>
        {/* Hide Login/Register inside admin area OR after login */}
        {!isLoggedIn && !inAdminArea && (
          <>
            <Link to="/login">Login</Link>{" | "}
            <Link to="/register">Register</Link>
          </>
        )}
      </div>

      <div>
        {/* Show Logout on the right inside admin pages when logged in */}
        {inAdminArea && isLoggedIn && (
          <button onClick={handleLogout}>Logout</button>
        )}
      </div>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <AppNav />

      <Routes>
        {/* Auth routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Admin nested routes */}
        <Route
          path="/admin"
          element={
            <RoleRoute allow={["ADMIN"]}>
              <AdminDashboard />
            </RoleRoute>
          }
        >
          <Route path="dashboard" element={<div>Welcome, Admin</div>} />
          <Route path="verification" element={<Verification />} />
          <Route path="users" element={<div>Users Page</div>} />
          <Route path="products" element={<div>Products Page</div>} />
          <Route path="suppliers" element={<div>Suppliers Page</div>} />
          <Route path="orders" element={<div>Orders Page</div>} />
        </Route>

        {/* Manager nested routes */}
        <Route
          path="/manager"
          element={
            <RoleRoute allow={["MANAGER"]}>
              <ManagerDashboard />
            </RoleRoute>
          }
        >
          <Route path="dashboard" element={<div>Welcome, Manager</div>} />
          {/* Add more manager-specific routes here */}
        </Route>

        {/* Supplier nested routes */}
        <Route
          path="/supplier"
          element={
            <RoleRoute allow={["SUPPLIER"]}>
              <SupplierDashboard />
            </RoleRoute>
          }
        >
          <Route path="dashboard" element={<div>Welcome, Supplier</div>} />
          {/* Add more supplier-specific routes here */}
        </Route>

        {/* Staff nested routes */}
        <Route
          path="/staff"
          element={
            <RoleRoute allow={["STAFF"]}>
              <StaffDashboard />
            </RoleRoute>
          }
        >
          <Route path="dashboard" element={<div>Welcome, Staff</div>} />
          {/* Add more staff-specific routes here */}
        </Route>

        {/* Customer nested routes */}
        <Route
          path="/customer"
          element={
            <RoleRoute allow={["CUSTOMER"]}>
              <CustomerDashboard />
            </RoleRoute>
          }
        >
          <Route path="dashboard" element={<div>Welcome, Customer</div>} />
          {/* Add more customer-specific routes here */}
        </Route>

        {/* Fallback */}
        <Route path="*" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
