import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus, getProducts } from "../../api";
import { Auth } from "../../auth";

export default function SupplierOrders() {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");
  const [statusUpdating, setStatusUpdating] = useState({});
  const [supplierUserId, setSupplierUserId] = useState(null);

  useEffect(() => {
    const user = Auth.getUser();
    if (user && user.role === "SUPPLIER") setSupplierUserId(user.id);
  }, []);

  useEffect(() => {
    if (!supplierUserId) return;
    loadOrders();
    getProducts().then(setProducts);
    // eslint-disable-next-line
  }, [supplierUserId]);

  const loadOrders = async () => {
    setErrorMsg("");
    try {
      const data = await getOrders();
      const supplierOrders = data.filter(order => order.supplierId === supplierUserId);
      setOrders(supplierOrders);
    } catch {
      setErrorMsg("Failed to load orders.");
    }
  };

  const getProductName = (id) => {
    const p = products.find(prod => prod.id === id);
    return p ? p.name : `#${id}`;
  };

  const handleStatusChange = async (orderId, newStatus) => {
    setStatusUpdating(s => ({ ...s, [orderId]: true }));
    setErrorMsg("");
    try {
      await updateOrderStatus(orderId, newStatus);
      setOrders(orders =>
        orders.map(order =>
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
    } catch {
      setErrorMsg("Failed to update order status.");
    }
    setStatusUpdating(s => ({ ...s, [orderId]: false }));
  };

  return (
    <div>
      <h2>My Orders</h2>
      {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Product(s)</th>
            <th>Quantity</th>
            <th>Total Amount</th> {/* Add this */}
            <th>Status</th>
            <th>Update Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.length === 0 && (
            <tr>
              <td colSpan={6}>No orders found.</td>
            </tr>
          )}
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>
                {order.items
                  ? order.items.map(i => getProductName(i.productId)).join(", ")
                  : "-"}
              </td>
              <td>
                {order.items
                  ? order.items.map(i => i.quantity).join(", ")
                  : "-"}
              </td>
              <td>{order.totalAmount}</td> {/* Show total amount */}
              <td>{order.status}</td>
              <td>
                <select
                  value={order.status}
                  disabled={statusUpdating[order.id]}
                  onChange={e => handleStatusChange(order.id, e.target.value)}
                >
                  <option value="PENDING">PENDING</option>
                  <option value="PROCESSING">PROCESSING</option>
                  <option value="SHIPPED">SHIPPED</option>
                  <option value="DELIVERED">DELIVERED</option>
                  <option value="CANCELLED">CANCELLED</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
