import React, { useEffect, useState } from "react";
import UserRegister from "../../components/User/UserRegister";
import UserRoleUpdate from "../../components/User/UserRoleUpdate";
import { Auth } from "../../auth";

// Modal component
function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

// UserList modal component (shows only verified users)
function UserListModal({ open, onClose }) {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!open) return;
    fetch("/api/users", {
      headers: { ...Auth.getAuthHeader() }
    })
      .then(res => {
        if (!res.ok) throw new Error("Unauthorized");
        return res.json();
      })
      .then(data => {
        if (Array.isArray(data)) {
          setUsers(data.filter(u => u.verified));
          setError("");
        } else {
          setUsers([]);
          setError("Failed to load users.");
        }
      })
      .catch(() => {
        setUsers([]);
        setError("Network error or unauthorized.");
      });
  }, [open]);

  if (!open) return null;
  if (error) return <Modal open={open} onClose={onClose}><div>{error}</div></Modal>;

  return (
    <Modal open={open} onClose={onClose}>
      <h2>Verified Users</h2>
      <ul>
        {users.map(u => (
          <li key={u.id}>
            {u.username} ({u.role}) - {u.email}
          </li>
        ))}
      </ul>
    </Modal>
  );
}

export default function UsersPage() {
  const [users, setUsers] = useState([]);
  const [showRegister, setShowRegister] = useState(false);
  const [showUserList, setShowUserList] = useState(false);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const rowsPerPage = 5;

  // Fetch all users for the table
  const fetchUsers = () => {
    fetch("/api/users", {
      headers: { ...Auth.getAuthHeader() }
    })
      .then(res => {
        if (!res.ok) throw new Error("Unauthorized");
        return res.json();
      })
      .then(data => setUsers(Array.isArray(data) ? data : []))
      .catch(() => setUsers([]));
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Delete user
  const handleDelete = async id => {
    try {
      await fetch(`/api/users/${id}`, {
        method: "DELETE",
        headers: { ...Auth.getAuthHeader() }
      });
      fetchUsers();
    } catch {
      alert("Failed to delete user.");
    }
  };

  // Show pending verifications
  const pending = users.filter(
    u => ["MANAGER", "STAFF", "SUPPLIER"].includes(u.role) && !u.verified
  );

  // Filter users by username
  const filteredUsers = users.filter(u =>
    u.username?.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredUsers.length / rowsPerPage);
  const paginatedUsers = filteredUsers.slice(
    (page - 1) * rowsPerPage,
    page * rowsPerPage
  );

  // Reset to first page on search
  useEffect(() => {
    setPage(1);
  }, [search]);

  return (
    <div style={{ width: "100%", display: "flex", flexDirection: "column", alignItems: "center" }}>
      <h2>User Management</h2>
      <nav style={{ marginBottom: 12 }}>
        <button onClick={() => setShowUserList(true)}>View All Users</button>{" | "}
        <button onClick={() => setShowRegister(true)}>Register New User</button>
      </nav>
      <div style={{ marginBottom: 12 }}>
        <input
          type="text"
          placeholder="Search user by username"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <button onClick={() => setSearch(search)}>Search</button>
      </div>

      <UserListModal open={showUserList} onClose={() => setShowUserList(false)} />

      {showRegister && (
        <Modal open={showRegister} onClose={() => setShowRegister(false)}>
          <UserRegister
            onSuccess={() => {
              setShowRegister(false);
              fetchUsers();
            }}
            onCancel={() => setShowRegister(false)}
          />
        </Modal>
      )}

      <h3>All Users</h3>
      <div style={{ width: "100%", overflowX: "auto" }}>
        <table border="1" cellPadding="8" style={{ width: "100%", minWidth: 700 }}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
              <th>Verified</th>
              <th>Update Role</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {paginatedUsers.map(u => (
              <tr key={u.id}>
                <td>{u.id}</td>
                <td>{u.name}</td>
                <td>{u.username}</td>
                <td>{u.email}</td>
                <td>{u.role}</td>
                <td>{u.verified ? "Yes" : "No"}</td>
                <td>
                  <UserRoleUpdate userId={u.id} />
                </td>
                <td>
                  <button onClick={() => handleDelete(u.id)}>Delete</button>
                </td>
              </tr>
            ))}
            {paginatedUsers.length === 0 && (
              <tr><td colSpan={8}>No users found</td></tr>
            )}
          </tbody>
        </table>
      </div>
      {/* Pagination controls */}
      <div style={{ margin: "16px 0", display: "flex", gap: 8 }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>
          Prev
        </button>
        <span>Page {page} of {totalPages || 1}</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages || totalPages === 0}>
          Next
        </button>
      </div>

      <h3>Pending Verifications</h3>
      {pending.length === 0 ? (
        <div>No pending verifications.</div>
      ) : (
        <ul>
          {pending.map(u => (
            <li key={u.id}>
              {u.name} ({u.role}) - {u.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
