import React, { useEffect, useState } from "react";
import axios from "axios";
import { Auth } from "../../auth"; // import Auth

// Use relative base so CRA proxy sends cookies
const api = axios.create({
  baseURL: "/api/admin",
  withCredentials: true, // ensure cookies are sent (works with proxy or full URL if CORS allows credentials)
});

// Add Basic Auth header to every request
api.interceptors.request.use(config => {
  return {
    ...config,
    headers: {
      ...config.headers,
      ...Auth.getAuthHeader()
    }
  };
});

export default function Verification() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    api.get("/pending-verifications")
      .then(res => setUsers(res.data))
      .catch(err => setError(err.response?.statusText || "Failed to load pending verifications"))
      .finally(() => setLoading(false));
  }, []);

  const handleVerify = async (id) => {
    try {
      await api.post(`/verify/${id}`);
      setUsers(users => users.map(u => u.id === id ? { ...u, verified: true } : u));
    } catch (err) {
      alert(err.response?.data?.message || "Failed to verify");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <h2>Pending Verifications</h2>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Verification</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr><td colSpan={5}>No pending verifications.</td></tr>
          ) : users.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>
                {u.verified
                  ? <span>Verified</span>
                  : <button onClick={() => handleVerify(u.id)}>Verify</button>
                }
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
