import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import UserList from "../../components/User/UserList";
import UserRegister from "../../components/User/UserRegister";
import UserRoleUpdate from "../../components/User/UserRoleUpdate";
import UserDelete from "../../components/User/UserDelete";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff", padding: 24, borderRadius: 8, minWidth: 400, boxShadow: "0 2px 16px rgba(0,0,0,0.15)", position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>✕</button>
        {children}
      </div>
    </div>
  );
}

export default function UsersPage() {
  const [users, setUsers] = useState([]);
  const [showList, setShowList] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  // Fetch all users
  useEffect(() => {
    fetch("/api/users")
      .then(res => res.json())
      .then(setUsers)
      .catch(() => setUsers([]));
  }, []);

  // Delete user
  const handleDelete = async id => {
    try {
      const res = await fetch(`/api/users/${id}`, { method: "DELETE" });
      if (!res.ok) {
        alert("Failed to delete user.");
        return;
      }
      setUsers(users => users.filter(u => u.id !== id));
    } catch {
      alert("Network error.");
    }
  };

  // Show pending verifications
  const pending = users.filter(
    u => ["MANAGER", "STAFF", "SUPPLIER"].includes(u.role) && !u.verified
  );

  return (
    <div>
      <h2>User Management</h2>
      <nav style={{ marginBottom: 12 }}>
        <button onClick={() => setShowList(true)}>View All Users</button>{" | "}
        <button onClick={() => setShowRegister(true)}>Register New User</button>
      </nav>

      <Modal open={showList} onClose={() => setShowList(false)}>
        <UserList />
      </Modal>
      <Modal open={showRegister} onClose={() => setShowRegister(false)}>
        <UserRegister />
      </Modal>

      <h3>All Users</h3>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Verified</th>
            <th>Update Role</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>{u.verified ? "Yes" : "No"}</td>
              <td>
                <UserRoleUpdate userId={u.id} />
              </td>
              <td>
                <button onClick={() => handleDelete(u.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Pending Verifications</h3>
      {pending.length === 0 ? (
        <div>No pending verifications.</div>
      ) : (
        <ul>
          {pending.map(u => (
            <li key={u.id}>
              {u.name} ({u.role}) - {u.email}
            </li>
          ))}
        </ul>
      )}

      {/* Remove the <Routes> block if you only want popups */}
    </div>
  );
}
