import React, { useEffect, useState } from "react";
import styles from "./UserProfile.module.css";

export default function UserProfile({ userId }) {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch(`/api/users/${userId}`, {
      headers: { Authorization: "Basic " + btoa("admin:adminpwd") }
    })
      .then(res => res.json())
      .then(setUser)
      .catch(() => setUser(null));
  }, [userId]);

  if (!user) return <div className={styles.profileContainer}>Loading...</div>;
  return (
    <div className={styles.profileContainer}>
      <div className={styles.profileTitle}>User Profile</div>
      <div className={styles.profileField}>Username: {user.username}</div>
      <div className={styles.profileField}>Name: {user.name}</div>
      <div className={styles.profileField}>Email: {user.email}</div>
      <div className={styles.profileField}>Role: {user.role}</div>
    </div>
  );
}
