import React, { useEffect, useState } from "react";
import { getSuppliers, updateSupplier } from "../../api";
import SupplierAdd from "../../components/Supplier/SupplierAdd";
import { Auth } from "../../auth";

export default function SupplierProfile() {
  const [supplier, setSupplier] = useState(null);
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    // Get current user's id
    const user = Auth.getUser();
    if (!user) return;

    // Fetch all suppliers and find the one linked to this user
    getSuppliers().then((suppliers) => {
      const mySupplier = suppliers.find((s) => s.userId === user.id);
      setSupplier(mySupplier || null);
    });
  }, []);

  const handleUpdate = async (form) => {
    if (!supplier) return;
    await updateSupplier(supplier.id, form);
    setSupplier({ ...supplier, ...form });
    setEditing(false);
  };

  if (!supplier) return <div>Loading...</div>;

  return (
    <div>
      <h2>My Profile</h2>
      {!editing ? (
        <div>
          <p>
            <b>Company Name:</b> {supplier.companyName}
          </p> 
          <p>
            <b>Email:</b> {supplier.email}
          </p>
          <p>
            <b>Phone:</b> {supplier.phone}
          </p>
          <p>
            <b>Address:</b> {supplier.address}
          </p>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </div>
      ) : (
        <SupplierAdd
          supplier={supplier}
          isEdit
          onAdd={handleUpdate}
          onCancel={() => setEditing(false)}
        />
      )}
    </div>
  );
}
