import React, { useEffect, useState } from "react";
import ProductAdd from "../../components/Product/ProductAdd";
import ProductEdit from "../../components/Product/ProductEdit";
import ProductDelete from "../../components/Product/ProductDelete";
import { getProducts, addProduct, updateProduct, deleteProduct } from "../../api";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function ProductPage() {
  const [products, setProducts] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [editProduct, setEditProduct] = useState(null);
  const [deleteProductObj, setDeleteProduct] = useState(null);
  const [showView, setShowView] = useState(false);
  const [msg, setMsg] = useState("");
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const rowsPerPage = 5;

  // Fetch all products
  useEffect(() => {
    getProducts()
      .then(setProducts)
      .catch(() => setProducts([]));
  }, []);

  // Add product
  const handleAdd = async (data) => {
    try {
      const prod = await addProduct(data);
      setProducts(p => [...p, prod]);
      setShowAdd(false);
      setMsg("Product added successfully.");
    } catch {
      setMsg("Failed to add product");
    }
  };

  // Delete product
  const handleDelete = async (id) => {
    try {
      await deleteProduct(id);
      setProducts(p => p.filter(pr => pr.id !== id));
      setDeleteProduct(null);
    } catch (err) {
      alert(err.message);
    }
  };

  // Filter products by name
  const filteredProducts = products.filter(p =>
    p.name?.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredProducts.length / rowsPerPage);
  const paginatedProducts = filteredProducts.slice(
    (page - 1) * rowsPerPage,
    page * rowsPerPage
  );

  // Reset to first page on search
  useEffect(() => {
    setPage(1);
  }, [search]);

  return (
    <div>
      <h2>Product Management</h2>
      <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: 12, marginBottom: 16 }}>
        <button onClick={() => setShowAdd(true)}>Add Product</button>
        <button onClick={() => setShowView(true)}>View Product</button>
        <input
          type="text"
          placeholder="Search product by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ marginLeft: 8 }}
        />
        <button onClick={() => setSearch(search)}>Search</button>
      </div>
      <div style={{ width: "100%", overflowX: "auto" }}>
        <table
          border="1"
          cellPadding="8"
          style={{
            width: "80%",
            minWidth: 700,
            background: "transparent",
            borderRadius: 8,
            borderCollapse: "separate",
            borderSpacing: 0,
          }}
        >
          <thead style={{ background: "#eae6c0" }}>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Category</th>
              <th>Supplier ID</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {paginatedProducts.map(p => (
              <tr key={p.id} style={{ background: "#eae6c0" }}>
                <td>{p.id}</td>
                <td>{p.name}</td>
                <td>{p.description}</td>
                <td>{p.price}</td>
                <td>{p.stock}</td>
                <td>{p.category}</td>
                <td>{p.supplierId}</td>
                <td>
                  <button onClick={() => setEditProduct(p)}>Edit</button>
                </td>
                <td>
                  <button onClick={() => setDeleteProduct(p)}>Delete</button>
                </td>
              </tr>
            ))}
            {paginatedProducts.length === 0 && (
              <tr style={{ background: "#eae6c0" }}>
                <td colSpan={9}>No products found</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      {/* Pagination controls */}
      <div style={{ margin: "16px 0", display: "flex", gap: 8, justifyContent: "center" }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>
          Prev
        </button>
        <span>Page {page} of {totalPages || 1}</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages || totalPages === 0}>
          Next
        </button>
      </div>

      {msg && <div style={{ color: msg.includes("Failed") ? "red" : "green" }}>{msg}</div>}

      <Modal open={showAdd} onClose={() => setShowAdd(false)}>
        <ProductAdd onAdd={handleAdd} onCancel={() => setShowAdd(false)} />
      </Modal>
      <Modal open={!!editProduct} onClose={() => setEditProduct(null)}>
        {editProduct && (
          <ProductEdit
            product={editProduct}
            onEdit={async (id, data) => {
              try {
                await updateProduct(id, data);
                setMsg("Product updated successfully.");
                setEditProduct(null);
                getProducts()
                  .then(setProducts)
                  .catch(() => setProducts([]));
              } catch (err) {
                setMsg("Failed to update product");
              }
            }}
            onCancel={() => setEditProduct(null)}
          />
        )}
      </Modal>
      <Modal open={!!deleteProductObj} onClose={() => setDeleteProduct(null)}>
        {deleteProductObj && (
          <ProductDelete
            product={deleteProductObj}
            onDelete={() => handleDelete(deleteProductObj.id)}
            onCancel={() => setDeleteProduct(null)}
          />
        )}
      </Modal>
      {/* Product List Popup */}
      <Modal open={showView} onClose={() => setShowView(false)}>
        <h3>Product List</h3>
        <ul>
          {products.map(p => (
            <li key={p.id}>
              {p.name} - {p.description} - ${p.price} - Stock: {p.stock} - {p.category}
            </li>
          ))}
          {products.length === 0 && <li>No products loaded</li>}
        </ul>
      </Modal>
    </div>
  );
}
