import React from "react";
import { Link, Outlet, useLocation } from "react-router-dom";
import "./admin.css";

export default function AdminDashboard() {
  const location = useLocation();

  return (
    <div className="admin-dashboard">
      {/* Top Header Bar */}
      <header className="admin-header-bar">
        <h1 className="admin-header-title">ADMIN</h1>
        <nav className="admin-nav">
          <Link to="/admin/dashboard" className={location.pathname === "/admin/dashboard" ? "active" : ""}>Dashboard</Link>
          <Link to="/admin/users" className={location.pathname === "/admin/users" ? "active" : ""}>Users</Link>
          <Link to="/admin/products" className={location.pathname === "/admin/products" ? "active" : ""}>Products</Link>
          <Link to="/admin/suppliers" className={location.pathname === "/admin/suppliers" ? "active" : ""}>Suppliers</Link>
          <Link to="/admin/orders" className={location.pathname === "/admin/orders" ? "active" : ""}>Orders</Link>
          <Link to="/admin/verification" className={location.pathname === "/admin/verification" ? "active" : ""}>Verification</Link>
        </nav>
      </header>

      {/* Centered Content */}
      <div className="admin-layout">
        <div className="admin-card">
          <Outlet />
        </div>
      </div>
    </div>
  );
}
