import React, { useEffect, useState } from "react";
import { addSupplier, updateSupplier, getUsers } from "../../api";

export default function SupplierAdd({ onAdd, onCancel, supplier, isEdit }) {
  const [form, setForm] = useState({
    companyName: "",
    email: "",
    phone: "",
    address: "",
    userId: ""
  });
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    // Fetch users with role SUPPLIER
    getUsers().then(data => {
      // Filter users: role SUPPLIER and not already linked to a supplier, or the current linked user
      setUsers(
        data.filter(
          u =>
            u.role === "SUPPLIER" &&
            (!u.supplierId || (supplier && supplier.user && u.id === supplier.user.id))
        )
      );
    });
    if (supplier) {
      setForm({
        companyName: supplier.companyName || "",
        email: supplier.email || "",
        phone: supplier.phone || "",
        address: supplier.address || "",
        userId: supplier.user ? supplier.user.id : ""
      });
    } else {
      setForm({
        companyName: "",
        email: "",
        phone: "",
        address: "",
        userId: ""
      });
    }
    setMsg("");
  }, [supplier, isEdit]);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const payload = { ...form, user: { id: form.userId } };
      if (isEdit) {
        await updateSupplier(supplier.id, payload);
        setMsg("Supplier updated");
      } else {
        await addSupplier(payload);
        setMsg("Supplier added");
        setForm({ companyName: "", email: "", phone: "", address: "", userId: "" });
      }
      if (onAdd) onAdd(payload);
    } catch (err) {
      setMsg(isEdit ? "Supplier name already exists. Please try again." : "Failed to add supplier. Please try again.");
    }
  };

  return (
    <form onSubmit={onSubmit} style={{ display: "flex", flexDirection: "column", gap: 12, minWidth: 300 }}>
      <input name="companyName" placeholder="Company Name" value={form.companyName} onChange={onChange} required />
      <input name="email" type="email" placeholder="Email" value={form.email} onChange={onChange} required />
      <input name="phone" placeholder="Phone" value={form.phone} onChange={onChange} required />
      <input name="address" placeholder="Address" value={form.address} onChange={onChange} required />
      <select name="userId" value={form.userId} onChange={onChange} required>
        <option value="">Select Supplier User</option>
        {users.map(u => (
          <option key={u.id} value={u.id}>
            {u.username} ({u.email})
          </option>
        ))}
      </select>
      <div style={{ display: "flex", gap: 8 }}>
        <button type="submit">{isEdit ? "Save" : "Add Supplier"}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
      <div style={{ color: msg ? "red" : undefined }}>
        {msg}
      </div>
    </form>
  );
}
