import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus, deleteOrder } from "../../api";

export default function OrderPage() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("");

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    getOrders().then(setOrders).catch(() => setOrders([]));
  };

  const handleStatusChange = async (orderId, status) => {
    try {
      await updateOrderStatus(orderId, status);
      setOrders(orders =>
        orders.map(o =>
          o.id === orderId ? { ...o, status } : o
        )
      );
    } catch {
      alert("Failed to update order status.");
    }
  };

  const handleDelete = async (orderId) => {
    try {
      await deleteOrder(orderId);
      setOrders(orders => orders.filter(o => o.id !== orderId));
    } catch {
      alert("Failed to delete order.");
    }
  };

  // Filter and search logic
  const filteredOrders = orders.filter(order =>
    (statusFilter ? order.status === statusFilter : true) &&
    (search
      ? (order.customerName || order.customer?.name || "")
          .toLowerCase()
          .includes(search.toLowerCase()) ||
        String(order.id).includes(search)
      : true)
  );

  return (
    <div>
      <h2>Order Management</h2>
      <div style={{ margin: "12px 0" }}>
        <input
          type="text"
          placeholder="Search by customer or order ID"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <select
          value={statusFilter}
          onChange={e => setStatusFilter(e.target.value)}
          style={{ marginRight: 8 }}
        >
          <option value="">All Statuses</option>
          <option value="PENDING">Pending</option>
          <option value="APPROVED">Approved</option>
          <option value="SHIPPED">Shipped</option>
          <option value="COMPLETED">Completed</option>
          <option value="CANCELLED">Cancelled</option>
        </select>
        <button onClick={fetchOrders}>Refresh</button>
      </div>
      <table border="1" cellPadding="8" style={{ width: "80%", background: "#fff", borderRadius: 8 }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Customer</th>
            <th>Date</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredOrders.map(order => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.customerName || order.customer?.name}</td>
              <td>{order.date || order.createdAt}</td>
              <td>
                <select
                  value={order.status}
                  onChange={e => handleStatusChange(order.id, e.target.value)}
                >
                  <option value="PENDING">Pending</option>
                  <option value="APPROVED">Approved</option>
                  <option value="SHIPPED">Shipped</option>
                  <option value="COMPLETED">Completed</option>
                  <option value="CANCELLED">Cancelled</option>
                </select>
              </td>
              <td>
                <button onClick={() => setSelectedOrder(order)}>View</button>
                <button onClick={() => handleDelete(order.id)} style={{ marginLeft: 8, color: "red" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {filteredOrders.length === 0 && (
            <tr>
              <td colSpan={5}>No orders found</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Order details modal */}
      {selectedOrder && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
        }}>
          <div style={{
            background: "#fff", padding: "2rem", borderRadius: "12px", minWidth: "400px", maxWidth: "480px", boxShadow: "0 2px 16px rgba(0,0,0,0.15)", position: "relative"
          }}>
            <button style={{ position: "absolute", top: 8, right: 12 }} onClick={() => setSelectedOrder(null)}>✕</button>
            <h3>Order Details</h3>
            <pre>{JSON.stringify(selectedOrder, null, 2)}</pre>
          </div>
        </div>
      )}
    </div>
  );
}
