import React, { useState } from "react";
import { addProduct } from "../../api";

export default function ProductAdd() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    price: "",
    stock: "",
    category: "ELECTRONICS",
    supplierId: ""
  });
  const [msg, setMsg] = useState("");

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      await addProduct({ ...form, price: +form.price, stock: +form.stock });
      setMsg("Product added");
      setForm({ name: "", description: "", price: "", stock: "", category: "ELECTRONICS", supplierId: "" });
    } catch {
      setMsg("Failed to add product");
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input name="name" placeholder="Name" value={form.name} onChange={onChange} required />
      <input name="description" placeholder="Description" value={form.description} onChange={onChange} />
      <input name="price" type="number" step="0.01" placeholder="Price" value={form.price} onChange={onChange} required />
      <input name="stock" type="number" placeholder="Stock" value={form.stock} onChange={onChange} required />
      <input
        name="supplierId"
        type="number"
        placeholder="Supplier ID"
        value={form.supplierId || ""}
        onChange={onChange}
        required
      />
      <select name="category" value={form.category} onChange={onChange}>
        <option value="ELECTRONICS">ELECTRONICS</option>
        <option value="GROCERY">GROCERY</option>
        <option value="CLOTHING">CLOTHING</option>
      </select>
      <button type="submit">Add Product</button>
      <div>{msg}</div>
    </form>
  );
}
