import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus, deleteOrder, getSuppliers, assignSupplierToOrder } from "../../api";

export default function OrderPage() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [suppliers, setSuppliers] = useState([]);
  const [page, setPage] = useState(1);
  const rowsPerPage = 5;

  useEffect(() => {
    fetchOrders();
    fetchSuppliers();
  }, []);

  const fetchOrders = () => {
    getOrders().then(setOrders).catch(() => setOrders([]));
  };

  const fetchSuppliers = () => {
    getSuppliers().then(setSuppliers).catch(() => setSuppliers([]));
  };

  const handleStatusChange = async (orderId, status) => {
    try {
      await updateOrderStatus(orderId, status);
      setOrders(orders =>
        orders.map(o =>
          o.id === orderId ? { ...o, status } : o
        )
      );
    } catch {
      alert("Failed to update order status.");
    }
  };

  const handleDelete = async (orderId) => {
    try {
      await deleteOrder(orderId);
      setOrders(orders => orders.filter(o => o.id !== orderId));
    } catch {
      alert("Failed to delete order.");
    }
  };

  const handleAssignSupplier = async (orderId, userId) => {
    try {
      await assignSupplierToOrder(orderId, Number(userId));
      // Optionally refresh orders here
    } catch {
      alert("Failed to assign supplier.");
    }
  };

  // Filter and search logic
  const filteredOrders = orders.filter(order =>
    (statusFilter ? order.status === statusFilter : true) &&
    (search
      ? (order.customerName || order.customer?.name || "")
          .toLowerCase()
          .includes(search.toLowerCase()) ||
        String(order.id).includes(search)
      : true)
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredOrders.length / rowsPerPage);
  const paginatedOrders = filteredOrders.slice(
    (page - 1) * rowsPerPage,
    page * rowsPerPage
  );

  // Reset to first page on search or filter
  useEffect(() => {
    setPage(1);
  }, [search, statusFilter]);

  return (
    <div>
      <h2>Order Management</h2>
      <div style={{ margin: "12px 0" }}>
        <input
          type="text"
          placeholder="Search by customer or order ID"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <select
          value={statusFilter}
          onChange={e => setStatusFilter(e.target.value)}
          style={{ marginRight: 8 }}
        >
          <option value="">All Statuses</option>
          <option value="PENDING">Pending</option>
          <option value="APPROVED">Approved</option>
          <option value="SHIPPED">Shipped</option>
          <option value="COMPLETED">Completed</option>
          <option value="CANCELLED">Cancelled</option>
        </select>
        <button onClick={fetchOrders}>Refresh</button>
      </div>
      <div style={{ width: "100%", display: "flex", justifyContent: "center" }}>
        <table
          border="1"
          cellPadding="8"
          style={{
            width: "80%",
            minWidth: 700,
            background: "#eae6c0",
            borderRadius: 8,
            margin: "0 auto"
          }}
        >
          <thead style={{ background: "#eae6c0" }}>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Date</th>
              <th>Status</th>
              <th>Supplier</th>
              <th>View</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {paginatedOrders.map(order => (
              <tr key={order.id} style={{ background: "#eae6c0" }}>
                <td>{order.id}</td>
                <td>{order.customerName || order.customer?.name}</td>
                <td>{order.date || order.createdAt}</td>
                <td>
                  <select
                    value={order.status}
                    onChange={e => handleStatusChange(order.id, e.target.value)}
                  >
                    <option value="PENDING">Pending</option>
                    <option value="APPROVED">Approved</option>
                    <option value="SHIPPED">Shipped</option>
                    <option value="COMPLETED">Completed</option>
                    <option value="CANCELLED">Cancelled</option>
                  </select>
                </td>
                <td>
                  <select
                    value={order.supplierId || ""}
                    onChange={async e => {
                      const userId = e.target.value;
                      await handleAssignSupplier(order.id, userId);
                      setOrders(orders =>
                        orders.map(o =>
                          o.id === order.id ? { ...o, supplierId: Number(userId) } : o
                        )
                      );
                    }}
                  >
                    <option value="">Select Supplier</option>
                    {suppliers.map(s => (
                      <option key={s.id} value={s.userId}>
                        {s.companyName} ({s.userId})
                      </option>
                    ))}
                  </select>
                  {order.supplierId &&
                    (() => {
                      const supplier = suppliers.find(s => s.userId === order.supplierId);
                      return supplier ? (
                        <span style={{ marginLeft: 8, fontWeight: "bold" }}>
                          {supplier.companyName}
                        </span>
                      ) : null;
                    })()
                  }
                </td>
                <td>
                  <button onClick={() => setSelectedOrder(order)}>View</button>
                </td>
                <td>
                  <button onClick={() => handleDelete(order.id)} style={{ color: "red" }}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {paginatedOrders.length === 0 && (
              <tr style={{ background: "#eae6c0" }}>
                <td colSpan={7}>No orders found</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      {/* Pagination controls */}
      <div style={{ margin: "16px 0", display: "flex", gap: 8, justifyContent: "center" }}>
        <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>
          Prev
        </button>
        <span>Page {page} of {totalPages || 1}</span>
        <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages || totalPages === 0}>
          Next
        </button>
      </div>

      {/* Order details modal */}
      {selectedOrder && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
        }}>
          <div style={{
            background: "#fff", padding: "2rem", borderRadius: "12px", minWidth: "400px", maxWidth: "480px", boxShadow: "0 2px 16px rgba(0,0,0,0.15)", position: "relative"
          }}>
            <button style={{ position: "absolute", top: 8, right: 12 }} onClick={() => setSelectedOrder(null)}>âœ•</button>
            <h3>Order Details</h3>
            <pre>{JSON.stringify(selectedOrder, null, 2)}</pre>
          </div>
        </div>
      )}
    </div>
  );
}
