import React, { useEffect, useState } from "react";
import { getSuppliers, addSupplier, updateSupplier, deleteSupplier } from "../../api";
import SupplierAdd from "../../components/Supplier/SupplierAdd";

function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.3)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999
    }}>
      <div style={{
        background: "#fff",
        padding: "2rem",
        borderRadius: "12px",
        minWidth: "400px",
        maxWidth: "480px",
        boxShadow: "0 2px 16px rgba(0,0,0,0.15)",
        position: "relative"
      }}>
        <button style={{ position: "absolute", top: 8, right: 12 }} onClick={onClose}>âœ•</button>
        {children}
      </div>
    </div>
  );
}

export default function SupplierPage() {
  const [suppliers, setSuppliers] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [editSupplier, setEditSupplier] = useState(null);
  const [msg, setMsg] = useState("");
  const [search, setSearch] = useState("");

  useEffect(() => {
    getSuppliers()
      .then(setSuppliers)
      .catch(() => setSuppliers([]));
  }, []);

  // Clear message when supplier list changes (after add/edit/delete)
  useEffect(() => {
    setMsg("");
  }, [suppliers]);

  // Add supplier
  const handleAdd = async (data) => {
    try {
      const supplier = await addSupplier(data);
      setSuppliers(s => [...s, supplier]);
      setShowAdd(false);
    } catch (err) {
      if (err.message && err.message.startsWith("409")) {
        setMsg("A supplier with this company name or email already exists.");
      } else {
        setMsg("Failed to add supplier. Please try again.");
      }
    }
  };

  // Edit supplier
  const handleEdit = async (id, data) => {
    try {
      const updated = await updateSupplier(id, data);
      setSuppliers(s => s.map(sup => sup.id === id ? updated : sup));
      setEditSupplier(null);
      setMsg("Supplier updated successfully.");
    } catch {
      setMsg("Failed to update supplier");
    }
  };

  // Delete supplier
  const handleDelete = async (id) => {
    try {
      await deleteSupplier(id);
      setSuppliers(s => s.filter(sup => sup.id !== id));
      setMsg("Supplier deleted successfully.");
    } catch {
      setMsg("Failed to delete supplier");
    }
  };

  // Filter suppliers by company name
  const filteredSuppliers = suppliers.filter(s =>
    s.companyName?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Supplier Management</h2>
      <button onClick={() => { setShowAdd(true); setMsg(""); }}>Add Supplier</button>
      <div style={{ margin: "12px 0" }}>
        <input
          type="text"
          placeholder="Search supplier by company name"
          value={search}
          onChange={e => {
            setSearch(e.target.value);
            setMsg(""); // Optionally clear message on search
          }}
        />
        <button onClick={() => setSearch(search)}>Search</button>
      </div>
      <table border="1" cellPadding="8" style={{ width: "100%", background: "#fff", borderRadius: 8 }}>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {filteredSuppliers.map(s => (
            <tr key={s.id}>
              <td>{s.companyName}</td>
              <td>{s.email}</td>
              <td>{s.phone}</td>
              <td>
                <button onClick={() => setEditSupplier(s)}>Edit</button>
              </td>
              <td>
                <button onClick={() => handleDelete(s.id)}>Delete</button>
              </td>
            </tr>
          ))}
          {filteredSuppliers.length === 0 && (
            <tr><td colSpan={5}>No suppliers found</td></tr>
          )}
        </tbody>
      </table>
      {msg && !msg.startsWith("Failed") && (
        <div style={{ color: "green" }}>{msg}</div>
      )}

      {/* Add Supplier Modal */}
      <Modal open={showAdd} onClose={() => { setShowAdd(false); setMsg(""); }}>
        <SupplierAdd
          key={showAdd ? Date.now() : undefined}
          onAdd={handleAdd}
          onCancel={() => { setShowAdd(false); setMsg(""); }}
        />
      </Modal>

      {/* Edit Supplier Modal */}
      <Modal open={!!editSupplier} onClose={() => { setEditSupplier(null); setMsg(""); }}>
        {editSupplier && (
          <SupplierAdd
            supplier={editSupplier}
            onAdd={(data) => handleEdit(editSupplier.id, data)}
            onCancel={() => { setEditSupplier(null); setMsg(""); }}
            isEdit
          />
        )}
      </Modal>
    </div>
  );
}
