import React, { useEffect, useState } from "react";
import { getOrders, updateOrderStatus } from "../../api";
import OrderStatus from "../../components/Order/OrderStatusUpdate";
import { Navigate } from "react-router-dom";
import { Auth } from "../../auth";

const STATUSES = ["PENDING", "APPROVED", "SHIPPED", "COMPLETED", "CANCELLED"];
const ROWS_PER_PAGE = 5;

export default function ManagerOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [page, setPage] = useState(1);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getOrders();
      setOrders(data);
    } catch (err) {
      setError("Failed to load orders.");
    }
    setLoading(false);
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await updateOrderStatus(orderId, newStatus);
      setOrders(orders =>
        orders.map(order =>
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  // Filter and pagination logic
  const filteredOrders = orders.filter(order =>
    statusFilter ? order.status === statusFilter : true
  );
  const totalPages = Math.ceil(filteredOrders.length / ROWS_PER_PAGE);
  const paginatedOrders = filteredOrders.slice(
    (page - 1) * ROWS_PER_PAGE,
    page * ROWS_PER_PAGE
  );

  // Reset to first page on filter change
  useEffect(() => {
    setPage(1);
  }, [statusFilter]);

  const user = Auth.getCurrentUser();
  if (!user || user.role !== "MANAGER") {
    return <Navigate to="/login" replace />;
  }

  if (loading) return <div>Loading orders...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <h2 style={{ textAlign: "center", marginBottom: 24 }}>Orders</h2>
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", gap: 16, marginBottom: 24 }}>
        <label>
          <span style={{ marginRight: 8, fontWeight: 500 }}>Filter by Status:</span>
          <select
            value={statusFilter}
            onChange={e => setStatusFilter(e.target.value)}
            style={{
              background: "transparent",
              border: "1px solid #FF0078",
              color: "#FF0078",
              padding: "6px 16px",
              borderRadius: "6px"
            }}
          >
            <option value="">All Statuses</option>
            {STATUSES.map(status => (
              <option key={status} value={status}>{status.charAt(0) + status.slice(1).toLowerCase()}</option>
            ))}
          </select>
        </label>
      </div>
      <table border="1" cellPadding="8" style={{ width: "80%", margin: "0 auto", borderCollapse: "collapse", background: "transparent" }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Customer</th>
            <th>Date</th>
            <th>Status</th>
            <th>Total Amount</th>
          </tr>
        </thead>
        <tbody>
          {paginatedOrders.map(order =>
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.customerName}</td>
              <td>{order.createdAt ? new Date(order.createdAt).toLocaleString() : ""}</td>
              <td style={{ display: "flex", alignItems: "center", gap: 8, background: "transparent" }}>
                <span style={{ fontWeight: 500 }}>{order.status.charAt(0) + order.status.slice(1).toLowerCase()}:</span>
                <OrderStatus order={order} onStatusChange={handleStatusChange} />
              </td>
              <td>{order.totalAmount}</td>
            </tr>
          )}
          {paginatedOrders.length === 0 && (
            <tr>
              <td colSpan={5} style={{ textAlign: "center" }}>No orders found</td>
            </tr>
          )}
        </tbody>
      </table>
      {/* Pagination Controls */}
      <div style={{ margin: "24px 0", display: "flex", gap: 8, justifyContent: "center" }}>
        <button
          onClick={() => setPage(p => Math.max(1, p - 1))}
          disabled={page === 1}
          style={{
            background: "transparent",
            border: "1px solid #FF0078",
            color: "#FF0078",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: page === 1 ? "not-allowed" : "pointer"
          }}
        >
          Prev
        </button>
        <span>Page {page} of {totalPages || 1}</span>
        <button
          onClick={() => setPage(p => Math.min(totalPages, p + 1))}
          disabled={page === totalPages || totalPages === 0}
          style={{
            background: "transparent",
            border: "1px solid #FF0078",
            color: "#FF0078",
            padding: "6px 16px",
            borderRadius: "6px",
            cursor: (page === totalPages || totalPages === 0) ? "not-allowed" : "pointer"
          }}
        >
          Next
        </button>
      </div>
    </div>
  );
}
