import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import UserList from "../../components/User/UserList";
import UserRegister from "../../components/User/UserRegister";
import UserRoleUpdate from "../../components/User/UserRoleUpdate";
import UserDelete from "../../components/User/UserDelete";

export default function UsersPage() {
  const [users, setUsers] = useState([]);

  // Fetch all users
  useEffect(() => {
    fetch("/api/users")
      .then(res => res.json())
      .then(setUsers)
      .catch(() => setUsers([]));
  }, []);

  // Delete user
  const handleDelete = async id => {
    try {
      const res = await fetch(`/api/users/${id}`, { method: "DELETE" });
      if (!res.ok) {
        alert("Failed to delete user.");
        return;
      }
      setUsers(users => users.filter(u => u.id !== id));
    } catch {
      alert("Network error.");
    }
  };

  // Show pending verifications
  const pending = users.filter(
    u => ["MANAGER", "STAFF", "SUPPLIER"].includes(u.role) && !u.verified
  );

  return (
    <div>
      <h2>User Management</h2>

      {/* Shortcuts to sub-pages (example id to replace with actual selections) */}
      <nav style={{ marginBottom: 12 }}>
        <Link to="list">View All Users</Link> |{" "}
        <Link to="register">Register New User</Link> |{" "}
        <span>Assign Roles: open from user row</span> |{" "}
        <span>Delete Users: open from user row</span>
      </nav>

      {/* Keep your integrated table, or add nested routes below if you want separate pages */}
      <h3>All Users</h3>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Verified</th>
            <th>Update Role</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.username}</td>
              <td>{u.email}</td>
              <td>{u.role}</td>
              <td>{u.verified ? "Yes" : "No"}</td>
              <td>
                <UserRoleUpdate userId={u.id} />
              </td>
              <td>
                <button onClick={() => handleDelete(u.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Pending Verifications</h3>
      <ul>
        {pending.map(u => (
          <li key={u.id}>
            {u.name} ({u.role}) - {u.email}
          </li>
        ))}
      </ul>

      <Routes>
        <Route path="list" element={<UserList />} />
        <Route path="register" element={<UserRegister />} />
        <Route path="role/:id" element={<UserRoleUpdate />} />
        <Route path="delete/:id" element={<UserDelete />} />
      </Routes>
    </div>
  );
}
