import React, { useState } from "react";
// import { registerUser } from "../../api"; // import removed
import { Link, useNavigate } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    confirm: "",
    role: "CUSTOMER"
  });

  const [msg, setMsg] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});
  const nav = useNavigate();

  const onChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setMsg("");
    setFieldErrors(prev => ({ ...prev, [e.target.name]: "" }));
  };

  const validateClient = () => {
    const errors = {};
    if (!form.name.trim()) errors.name = "Name is required.";
    if (!form.username.trim()) errors.username = "Username is required.";
    if (!form.email.trim()) errors.email = "Email is required.";
    // Basic email check
    if (form.email && !/^\S+@\S+\.\S+$/.test(form.email)) errors.email = "Invalid email.";
    if (!form.password) errors.password = "Password is required.";
    if (form.password && form.password.length < 6) errors.password = "Password must be at least 6 characters.";
    if (form.password !== form.confirm) errors.confirm = "Passwords do not match.";
    return errors;
  };

  const extractErrorMessage = async (res) => {
    // Try JSON body first
    try {
      const data = await res.json();
      // Spring may return {message}, or {error}, or ProblemDetail structure
      return data.message || data.error || data.detail || JSON.stringify(data);
    } catch {
      // Fallback to text
      try {
        return await res.text();
      } catch {
        return "Registration failed.";
      }
    }
  };

  const onSubmit = async e => {
    e.preventDefault();
    setMsg("");
    setFieldErrors({});

    // Client-side validation
    const errors = validateClient();
    if (Object.keys(errors).length > 0) {
      setFieldErrors(errors);
      setMsg("Please fix the highlighted errors.");
      return;
    }

    // Call backend directly to capture precise error details
    try {
      const res = await fetch("/api/users/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: form.name,
          username: form.username,
          email: form.email,
          password: form.password,
          role: form.role
        })
      });

      if (!res.ok) {
        const errMsg = await extractErrorMessage(res);

        // Map common backend messages to user-friendly hints
        if (res.status === 409 && /username.*exists/i.test(errMsg)) {
          setFieldErrors(prev => ({ ...prev, username: "Username already registered." }));
          setMsg("Please use a different username.");
        } else if (res.status === 409 && /email.*exists/i.test(errMsg)) {
          setFieldErrors(prev => ({ ...prev, email: "Email already registered." }));
          setMsg("Please use a different email.");
        } else if (res.status === 400 && /password.*6/i.test(errMsg)) {
          setFieldErrors(prev => ({ ...prev, password: "Password must be at least 6 characters." }));
          setMsg("Please fix the highlighted errors.");
        } else if (res.status === 400 && /invalid role/i.test(errMsg)) {
          setFieldErrors(prev => ({ ...prev, role: "Invalid role." }));
          setMsg("Please pick a valid role.");
        } else {
          setMsg(errMsg || "Registration failed.");
        }
        return;
      }

      // Success
      setMsg("Registered successfully!");
      nav("/login");
    } catch {
      setMsg("Network error.");
    }
  };

  return (
    <div style={{ padding: 16 }}>
      <h2>Create Account</h2>
      <form onSubmit={onSubmit} noValidate>
        <div>
          <input
            name="name"
            placeholder="Name"
            value={form.name}
            onChange={onChange}
            required
          />
          {fieldErrors.name && <div style={{ color: "red" }}>{fieldErrors.name}</div>}
        </div>

        <div>
          <input
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={onChange}
            required
          />
          {fieldErrors.username && <div style={{ color: "red" }}>{fieldErrors.username}</div>}
        </div>

        <div>
          <input
            name="email"
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={onChange}
            required
          />
          {fieldErrors.email && <div style={{ color: "red" }}>{fieldErrors.email}</div>}
        </div>

        <div>
          <input
            name="password"
            type="password"
            placeholder="Password"
            value={form.password}
            onChange={onChange}
            required
          />
          {fieldErrors.password && <div style={{ color: "red" }}>{fieldErrors.password}</div>}
        </div>

        <div>
          <input
            name="confirm"
            type="password"
            placeholder="Confirm Password"
            value={form.confirm}
            onChange={onChange}
            required
          />
          {fieldErrors.confirm && <div style={{ color: "red" }}>{fieldErrors.confirm}</div>}
        </div>

        <div>
          <select name="role" value={form.role} onChange={onChange}>
            <option value="CUSTOMER">Customer</option>
            <option value="MANAGER">Manager</option>
            <option value="SUPPLIER">Supplier</option>
            <option value="STAFF">Staff</option>
            <option value="ADMIN">Admin</option>
          </select>
          {fieldErrors.role && <div style={{ color: "red" }}>{fieldErrors.role}</div>}
        </div>

        <br />
        <button type="submit">Register</button>
      </form>

      {msg && <div style={{ marginTop: 8, color: msg.includes("success") ? "green" : "red" }}>{msg}</div>}

      <div style={{ marginTop: 8 }}>
        Already have an account? <Link to="/login">Login</Link>
      </div>
    </div>
  );
}
