import React, { useState } from "react";
import { placeOrder } from "../../api";

export default function OrderPlace() {
  const [customerId, setCustomerId] = useState("");
  const [items, setItems] = useState([{ productId: "", quantity: "" }]);
  const [msg, setMsg] = useState("");

  const onItemChange = (idx, field, value) => {
    const copy = items.slice();
    copy[idx] = { ...copy[idx], [field]: value };
    setItems(copy);
  };

  const addItem = () => setItems([...items, { productId: "", quantity: "" }]);
  const removeItem = idx => setItems(items.filter((_, i) => i !== idx));

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const payload = {
        customerId: +customerId,
        items: items
          .filter(i => i.productId && i.quantity)
          .map(i => ({ productId: +i.productId, quantity: +i.quantity }))
      };
      await placeOrder(payload);
      setMsg("Order placed");
      setCustomerId("");
      setItems([{ productId: "", quantity: "" }]);
    } catch {
      setMsg("Failed to place order");
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <h3>Place Order</h3>
      <input
        type="number"
        placeholder="Customer ID"
        value={customerId}
        onChange={e => setCustomerId(e.target.value)}
        required
      />
      <div>
        {items.map((it, idx) => (
          <div key={idx} style={{ marginBottom: 8 }}>
            <input
              type="number"
              placeholder="Product ID"
              value={it.productId}
              onChange={e => onItemChange(idx, "productId", e.target.value)}
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={it.quantity}
              onChange={e => onItemChange(idx, "quantity", e.target.value)}
              required
            />
            <button type="button" onClick={() => removeItem(idx)}>Remove</button>
          </div>
        ))}
        <button type="button" onClick={addItem}>Add Item</button>
      </div>
      <button type="submit">Place Order</button>
      <div>{msg}</div>
    </form>
  );
}
