import React, { useEffect, useState } from "react";
// Make sure these API functions exist
// You may need to implement or adjust these API functions
import { getProducts, addProduct, updateProduct, deleteProduct } from "../../api";
export default function ManagerProducts() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);
  const [newStock, setNewStock] = useState("");
  const [showAddForm, setShowAddForm] = useState(false);
  const [addForm, setAddForm] = useState({ name: "", sku: "", price: "", stock: "", supplier: "" });

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch {
      // Handle error as needed
    }
  };

  const handleSearch = e => setSearch(e.target.value);

  const filteredProducts = products.filter(
    p =>
      p.name.toLowerCase().includes(search.toLowerCase()) ||
      (p.sku && p.sku.toLowerCase().includes(search.toLowerCase()))
  );

  const handleEditStock = product => {
    setEditingProduct(product);
    setNewStock(product.stock);
  };

  const handleUpdateStock = async () => {
    try {
      await updateProductStock(editingProduct.id, newStock);
      setEditingProduct(null);
      setNewStock("");
      loadProducts();
    } catch {
      // Handle error as needed
    }
  };

  const handleEditProduct = product => {
    setEditingProduct(product);
    setAddForm({
      name: product.name,
      sku: product.sku,
      price: product.price,
      stock: product.stock,
      supplier: product.supplier || ""
    });
  };

  const handleUpdateProduct = async () => {
    try {
      await editProduct(editingProduct.id, addForm);
      setEditingProduct(null);
      setAddForm({ name: "", sku: "", price: "", stock: "", supplier: "" });
      loadProducts();
    } catch {
      // Handle error as needed
    }
  };

  const handleAddProduct = async e => {
    e.preventDefault();
    try {
      await addProduct(addForm);
      setShowAddForm(false);
      setAddForm({ name: "", sku: "", price: "", stock: "", supplier: "" });
      loadProducts();
    } catch {
      // Handle error as needed
    }
  };

  return (
    <div>
      <h2>Products</h2>
      <div style={{ marginBottom: 16 }}>
        <input
          type="text"
          placeholder="Search by name or SKU"
          value={search}
          onChange={handleSearch}
          style={{ padding: 6, width: 220 }}
        />
        <button onClick={() => setShowAddForm(!showAddForm)} style={{ marginLeft: 16 }}>
          {showAddForm ? "Cancel" : "Add Product"}
        </button>
      </div>

      {showAddForm && (
        <form onSubmit={handleAddProduct} style={{ marginBottom: 24 }}>
          <input
            required
            placeholder="Name"
            value={addForm.name}
            onChange={e => setAddForm(f => ({ ...f, name: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            placeholder="SKU"
            value={addForm.sku}
            onChange={e => setAddForm(f => ({ ...f, sku: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Price"
            value={addForm.price}
            onChange={e => setAddForm(f => ({ ...f, price: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            required
            type="number"
            placeholder="Stock"
            value={addForm.stock}
            onChange={e => setAddForm(f => ({ ...f, stock: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <input
            placeholder="Supplier"
            value={addForm.supplier}
            onChange={e => setAddForm(f => ({ ...f, supplier: e.target.value }))}
            style={{ marginRight: 8 }}
          />
          <button type="submit">Add</button>
        </form>
      )}

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>SKU</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Supplier</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map(product =>
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.sku}</td>
              <td>{product.price}</td>
              <td>
                {editingProduct && editingProduct.id === product.id && typeof newStock !== "undefined" ? (
                  <>
                    <input
                      type="number"
                      value={newStock}
                      onChange={e => setNewStock(e.target.value)}
                      style={{ width: 60 }}
                    />
                    <button onClick={handleUpdateStock} style={{ marginLeft: 4 }}>Save</button>
                    <button onClick={() => setEditingProduct(null)} style={{ marginLeft: 4 }}>Cancel</button>
                  </>
                ) : (
                  <>
                    {product.stock}
                    <button onClick={() => handleEditStock(product)} style={{ marginLeft: 8 }}>Edit Stock</button>
                  </>
                )}
              </td>
              <td>{product.supplier || ""}</td>
              <td>
                {editingProduct && editingProduct.id === product.id ? (
                  <>
                    <button onClick={handleUpdateProduct}>Save Edit</button>
                    <button onClick={() => setEditingProduct(null)} style={{ marginLeft: 4 }}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEditProduct(product)}>Edit</button>
                )}
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
