import React from "react";
import { NavLink, Outlet, useNavigate } from "react-router-dom";
import "./manager.css";

export default function ManagerDashboard() {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Add your logout logic here (e.g., clear tokens)
    navigate("/login");
  };

  return (
    <div className="manager-layout">
      {/* Fixed Header Bar */}
      <header className="manager-header-bar">
        <div style={{ display: "flex", flexDirection: "column", alignItems: "flex-start" }}>
          <h1 className="manager-header-title">Manager</h1>
          <button
            onClick={handleLogout}
            title="Logout"
            style={{
              background: "none",
              border: "none",
              cursor: "pointer",
              marginTop: 8,
              marginLeft: 2,
              padding: 0,
              display: "flex",
              alignItems: "center"
            }}
          >
            {/* Logout SVG icon */}
            <svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="#fde9e9ff" strokeWidth="2"
              strokeLinecap="round" strokeLinejoin="round">
              <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
              <polyline points="16 17 21 12 16 7" />
              <line x1="21" y1="12" x2="9" y2="12" />
            </svg>
          </button>
        </div>
        <nav className="manager-nav">
          <NavLink to="/manager/dashboard">Dashboard</NavLink>
          <NavLink to="/manager/orders">Orders</NavLink>
          <NavLink to="/manager/products">Products</NavLink>
          <NavLink to="/manager/suppliers">Suppliers</NavLink>
        </nav>
      </header>

      {/* Main Card */}
      <main className="manager-card">
        <Outlet />
      </main>
    </div>
  );
}
