import React from "react";
import styles from "./ProductList.module.css";

export default function ProductList({ products = [], onEdit, onDelete }) {
  return (
    <div className={styles.listContainer}>
      <div className={styles.listTitle}>Products</div>
      <ul>
        {products.map(p => (
          <li key={p.id} className={styles.listItem}>
            {p.name} - ${p.price} - Stock: {p.stock} - {p.category}
            {onEdit && <button onClick={() => onEdit(p)}>Edit</button>}
            {onDelete && <button onClick={() => onDelete(p)}>Delete</button>}
          </li>
        ))}
      </ul>
      {products.length === 0 && <div>No products loaded</div>}
    </div>
  );
}
